import React, { useState, useEffect } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import axios from 'axios'; // Import axios for making HTTP requests

function Login({ show, handleClose }) {
  const [isSignUp, setIsSignUp] = useState(false); // State to toggle between login and signup
  const [userType, setUserType] = useState(''); // State for user type
  const [email, setEmail] = useState(''); // State for email
  const [password, setPassword] = useState(''); // State for password
  const [confirmPassword, setConfirmPassword] = useState(''); // State for confirm password
  const [mobileNumber, setMobileNumber] = useState(''); // State for mobile number
  const [name, setName] = useState(''); // State for name
  const [errorMessage, setErrorMessage] = useState(''); // State for error messages

  useEffect(() => {
    // Clear the form fields when the modal is closed
    if (!show) {
      setEmail('');
      setPassword('');
      setConfirmPassword('');
      setUserType('');
      setErrorMessage('');
      setMobileNumber('');
      setName('');
    }
  }, [show]);

  const handleToggle = () => {
    setIsSignUp(!isSignUp); // Toggle between login and signup
    setUserType(''); // Reset user type when toggling
    setErrorMessage(''); // Reset error message when toggling
  };

  const handleSubmitLogin = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/loginUser', {
        email,
        password,
      });
      alert('Login Successful');
      console.log('Token:', response.data.authToken);
      handleClose();
    } catch (error) {
      console.error('Login Error:', error);
      setErrorMessage('Login Failed: ' + error.response.data.message);
    }
  };

  const handleSubmitSignUp = async (event) => {
    event.preventDefault();
    if (password !== confirmPassword) {
      setErrorMessage('Passwords do not match!');
      return;
    }
    try {
      await axios.post('http://localhost:5000/api/createuser', {
        email,
        password,
        userType,
        mobileNumber,
        name
      });
      alert('Sign Up Successful');
      handleClose(); // Close modal on successful signup
    } catch (error) {
      console.error('Sign Up Error:', error);
      setErrorMessage('Sign Up Failed: ' + (error.response?.data?.message || error.message));
    }
  };

  return (
    <Modal show={show} onHide={handleClose} centered className="auth-modal">
      <Modal.Header closeButton>
        <Modal.Title>{isSignUp ? 'Sign Up' : 'Login'}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {errorMessage && (
          <div className="alert alert-danger" role="alert">
            {errorMessage}
          </div>
        )}
        {isSignUp ? (
          <Form onSubmit={handleSubmitSignUp}>
            <Form.Group className="mb-3" controlId="signUpEmail">
              <Form.Label>Email address</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter your email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="signUpPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Enter your password"
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="signUpConfirmPassword">
              <Form.Label>Confirm Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Confirm your password"
                required
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="signUpMobileNumber">
              <Form.Label>Mobile Number</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter your mobile number"
                required
                value={mobileNumber}
                onChange={(e) => setMobileNumber(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="signUpName">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter your name"
                required
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="userType">
              <Form.Label>Select User Type</Form.Label>
              <Form.Control
                as="select"
                value={userType}
                onChange={(e) => setUserType(e.target.value)}
                required
              >
                <option value="">Select...</option>
                <option value="student">Student</option>
                <option value="faculty">Faculty</option>
                <option value="event-manager">Event Manager</option>
              </Form.Control>
            </Form.Group>

            <Button variant="primary" type="submit">
              Sign Up
            </Button>
            <Button variant="link" onClick={handleToggle}>
              Already have an account? Login
            </Button>
          </Form>
        ) : (
          <Form onSubmit={handleSubmitLogin}>
            <Form.Group className="mb-3" controlId="loginEmail">
              <Form.Label>Email address</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter your email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>

            <Form.Group className="mb-3" controlId="loginPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Enter your password"
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>

            <Button variant="primary" type="submit">
              Login
            </Button>
            <Button variant="link" onClick={handleToggle}>
              New user? Register
            </Button>
          </Form>
        )}
      </Modal.Body>
    </Modal>
  );
}

export default Login;
