import React, { useState } from 'react';
import './EventFeedback.css'; // Style your component accordingly

const EventFeedback = ({ event }) => {
  const [rating, setRating] = useState(0); // State for star rating, initialize to 0
  const [feedbackText, setFeedbackText] = useState(''); // State for feedback text

  const handleRatingChange = (value) => {
    setRating(value); // Update rating state based on user input
  };

  const handleTextChange = (e) => {
    setFeedbackText(e.target.value); // Update feedback text state
  };

  const handleSubmit = () => {
    // Logic to handle feedback submission (e.g., send to server, update database)
    console.log(`Submitted feedback for ${event.name}: Rating - ${rating}, Text - ${feedbackText}`);
    // Reset state after submission if needed
    setRating(0);
    setFeedbackText('');
  };

  return (
    <div className="event-feedback">
      <img src={event.imageUrl} alt={event.name} />
      <h3>{event.name}</h3>
      <div className="rating">
        {[...Array(5)].map((_, index) => (
          <span
            key={index}
            className={index < rating ? 'star filled' : 'star'}
            onClick={() => handleRatingChange(index + 1)}
          >
            &#9733;
          </span>
        ))}
      </div>
      <textarea
        placeholder="Enter your feedback..."
        value={feedbackText}
        onChange={handleTextChange}
      />
      <button onClick={handleSubmit}>Submit Feedback</button>
    </div>
  );
};

export default EventFeedback;
