import React, { useEffect, useState } from 'react';
import './AdminVolunteer.css'; // Your custom CSS (if any)
import 'bootstrap/dist/css/bootstrap.min.css';

function EventPromotion() {
  const [eventVolunteerData, setEventVolunteerData] = useState([]);
  const [imageFiles, setImageFiles] = useState({});
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');

  useEffect(() => {
    fetch('http://localhost:5000/api/volunteers/events')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        console.log('Fetched event and volunteer data:', data);
        setEventVolunteerData(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching event and volunteer data:', error);
        setMessage('Error fetching data');
        setLoading(false);
      });
  }, []);

  const handleFileChange = (event, eventId) => {
    setImageFiles((prev) => ({
      ...prev,
      [eventId]: event.target.files[0],
    }));
  };

  const handleUpload = (eventId) => {
    const formData = new FormData();
    formData.append('image', imageFiles[eventId]);

    fetch(`http://localhost:5000/api/volunteers/events/${eventId}/upload`, {
      method: 'POST',
      body: formData,
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        console.log('Upload successful:', data);
        setMessage('Upload successful!');
        // Reset the file input
        setImageFiles((prev) => ({ ...prev, [eventId]: null }));
      })
      .catch((error) => {
        console.error('Error uploading image:', error);
        setMessage('Error uploading image');
      });
  };

  if (loading) {
    return <div className="text-center mt-5">Loading...</div>;
  }

  return (
    <div className="container mt-4">
      {message && <div className="alert alert-info">{message}</div>}
      <div className="row">
        {eventVolunteerData.map((eventData) => (
          <div key={eventData._id} className="col-md-12 mb-4">
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">{eventData.eventName}</h5>
                <input
                  type="file"
                  onChange={(e) => handleFileChange(e, eventData._id)}
                  className="form-control mb-2"
                />
                {imageFiles[eventData._id] && (
                  <div className="mb-2">
                    <strong>Selected file:</strong> {imageFiles[eventData._id].name}
                  </div>
                )}
                <button
                  onClick={() => handleUpload(eventData._id)}
                  className="btn btn-primary"
                  disabled={!imageFiles[eventData._id]} // Disable if no file selected
                >
                  Upload Image
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default EventPromotion;
