import React, { useState } from 'react';
import { Form, Button, Card, Modal } from 'react-bootstrap';

const EventPlanning = () => {
  const [name, setName] = useState('');
  const [date, setDate] = useState('');
  const [venue, setVenue] = useState('');
  const [events, setEvents] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [showProgramModal, setShowProgramModal] = useState(false);
  const [currentEventIndex, setCurrentEventIndex] = useState(null);
  const [programName, setProgramName] = useState('');
  const [programDate, setProgramDate] = useState('');
  const [programStartTime, setProgramStartTime] = useState('');
  const [EventEndDate, setEventEndDate] = useState('');
  const [programEndTime, setProgramEndTime] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const newEvent = { name, date, venue, eventPrograms: [] };
    setEvents([...events, newEvent]);
    setName('');
    setDate('');
    setVenue('');
    setShowForm(false);
  };

  const deleteEvent = (index) => {
    const updatedEvents = events.filter((_, i) => i !== index);
    setEvents(updatedEvents);
  };

  const addOrEditProgram = (e) => {
    e.preventDefault();
    const updatedEvents = [...events];
    if (currentEventIndex !== null) {
      const eventPrograms = updatedEvents[currentEventIndex].eventPrograms;
      eventPrograms.push({
        name: programName,
        date: programDate,
        startTime: programStartTime,
        endDate: EventEndDate,
        endTime: programEndTime,
      });
      updatedEvents[currentEventIndex].eventPrograms = eventPrograms;
      setEvents(updatedEvents);
    }
    setShowProgramModal(false);
    setProgramName('');
    setProgramDate('');
    setProgramStartTime('');
    setProgramEndDate('');
    setProgramEndTime('');
    setCurrentEventIndex(null);
  };

  const openProgramModal = (index) => {
    setCurrentEventIndex(index);
    setShowProgramModal(true);
  };

  return (
    <div>
      <Button variant="primary" onClick={() => setShowForm(!showForm)}>
        + Add Event
      </Button>
      {showForm && (
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="eventName">
            <Form.Label>Event Name</Form.Label>
            <Form.Control
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </Form.Group>

          <Form.Group controlId="eventStratDate">
            <Form.Label>eventStratDate</Form.Label>
            <Form.Control
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </Form.Group>

          <Form.Group controlId="EventEndDate">
              <Form.Label>End Date</Form.Label>
              <Form.Control
                type="date"
                value={EventEndDate}
                onChange={(e) => setEventEndDate(e.target.value)}
                required
              />
            </Form.Group>

          <Form.Group controlId="eventVenue">
            <Form.Label>Venue</Form.Label>
            <Form.Control
              type="text"
              value={venue}
              onChange={(e) => setVenue(e.target.value)}
              required
            />
          </Form.Group>

          <Button variant="primary" type="submit">
            Create Event
          </Button>
        </Form>
      )}

      <div className="mt-4">
        {events.map((event, eventIndex) => (
          <Card key={eventIndex} className="mb-3">
            <Card.Body>
              <Card.Title className="d-flex justify-content-between align-items-center">
                {event.name}
                <div>
                  <Button
                    variant="info"
                    size="sm"
                    className="mr-2"
                    onClick={() => openProgramModal(eventIndex)}
                  >
                    + Add Event Program
                  </Button>
                  <Button
                    variant="danger"
                    size="sm"
                    onClick={() => deleteEvent(eventIndex)}
                  >
                    <i className="bi bi-trash" />
                  </Button>
                </div>
              </Card.Title>
              <Card.Subtitle className="mb-2 text-muted">{event.date}</Card.Subtitle>
              <Card.Text>
                Venue: {event.venue}
              </Card.Text>
              {event.eventPrograms && event.eventPrograms.length > 0 && (
                <div>
                  <h5>Event Programs:</h5>
                  <ul>
                    {event.eventPrograms.map((program, programIndex) => (
                      <li key={programIndex}>
                        {program.name} - {program.date} {program.startTime} to {program.endDate} {program.endTime}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </Card.Body>
          </Card>
        ))}
      </div>

      <Modal show={showProgramModal} onHide={() => setShowProgramModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add Event Program</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={addOrEditProgram}>
            <Form.Group controlId="programName">
              <Form.Label>Program Name</Form.Label>
              <Form.Control
                type="text"
                value={programName}
                onChange={(e) => setProgramName(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="programDate">
              <Form.Label>Start Date</Form.Label>
              <Form.Control
                type="date"
                value={programDate}
                onChange={(e) => setProgramDate(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="programStartTime">
              <Form.Label>Start Time</Form.Label>
              <Form.Control
                type="time"
                value={programStartTime}
                onChange={(e) => setProgramStartTime(e.target.value)}
                required
              />
            </Form.Group>


            <Form.Group controlId="programEndTime">
              <Form.Label>End Time</Form.Label>
              <Form.Control
                type="time"
                value={programEndTime}
                onChange={(e) => setProgramEndTime(e.target.value)}
                required
              />
            </Form.Group>

            <Button variant="primary" type="submit">
              Save Program
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default EventPlanning;
