import React, { useState, useEffect } from 'react';
import { Button, Card, Modal, Form, Alert } from 'react-bootstrap';
import axios from 'axios';

const VolunteerManagement = () => {
  const [events, setEvents] = useState([]);
  const [showRegisterForm, setShowRegisterForm] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [collegeId, setCollegeId] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [registered, setRegistered] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [registrationCount, setRegistrationCount] = useState(0);

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/events/getEvents');
      setEvents(response.data);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const handleShowRegisterForm = (event) => {
    setSelectedEvent(event);
    setShowRegisterForm(true);
    checkRegistrationCount(event._id);
  };

  const checkRegistrationCount = async (eventId) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/volunteers/count/${eventId}`);
      setRegistrationCount(response.data.count);
      if (response.data.count >= 2 * selectedEvent.volunteersNeeded) {
        setRegistered(true);
      }
    } catch (error) {
      console.error('Error fetching registration count:', error);
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const newVolunteer = {
        eventId: selectedEvent._id,
        name,
        email,
        collegeId,
        phoneNumber,
      };
      await axios.post('http://localhost:5000/api/volunteers/register', newVolunteer);
      setAlertMessage('Registration successful!');
      setRegistered(true);
      fetchEvents(); // Refresh event data after registration
      checkRegistrationCount(selectedEvent._id); // Recheck registration count
    } catch (error) {
      console.error('Error registering volunteer:', error);
      setAlertMessage('Registration failed. Please try again.');
    }
  };

  return (
    <div className="container mt-4">
      <div className="event-cards-container">
        {events.map((event) => (
          <Card key={event._id} className="event-card">
            {event.imageUrl && (
              <Card.Img variant="top" src={event.imageUrl} alt={event.eventName} />
            )}
            <Card.Body>
              <Card.Title>{event.eventName}</Card.Title>
              <Button
                variant="primary"
                onClick={() => handleShowRegisterForm(event)}
                disabled={registered}
              >
                {registered ? 'Already Registered' : 'Register'}
              </Button>
            </Card.Body>
          </Card>
        ))}
      </div>

      <Modal show={showRegisterForm} onHide={() => setShowRegisterForm(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Register for {selectedEvent?.eventName}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleRegister}>
            <Form.Group controlId="name">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </Form.Group>
            <Form.Group controlId="email">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </Form.Group>
            <Form.Group controlId="collegeId">
              <Form.Label>College ID</Form.Label>
              <Form.Control
                type="text"
                value={collegeId}
                onChange={(e) => setCollegeId(e.target.value)}
                required
              />
            </Form.Group>
            <Form.Group controlId="phoneNumber">
              <Form.Label>Phone Number</Form.Label>
              <Form.Control
                type="text"
                value={phoneNumber}
                onChange={(e) => setPhoneNumber(e.target.value)}
                required
              />
            </Form.Group>
            {alertMessage && <Alert variant="info">{alertMessage}</Alert>}
            <Button variant="primary" type="submit" disabled={registered}>
              {registered ? 'Already Registered' : 'Register'}
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default VolunteerManagement;
