// src/firebase.js
import { initializeApp } from 'firebase/app';
import { getMessaging, onMessage } from 'firebase/messaging';

const firebaseConfig = {
  apiKey: "AIzaSyAD2mE_w_CDDEIinVpplP4iK9ncllAgiW8",
  authDomain: "pwa-app-ccb69.firebaseapp.com",
  databaseURL: "https://pwa-app-ccb69.firebaseio.com",
  projectId: "pwa-app-ccb69",
  storageBucket: "pwa-app-ccb69.appspot.com",
  messagingSenderId: "87598063745",
  appId: "1:875918063745:web:366bb079c60e9d08cdb580"
};

const app = initializeApp(firebaseConfig);
const messaging = getMessaging(app);

export const generateToken = (registration) => {
  return messaging.getToken({
    vapidKey: "BF9Q0ytyvFzr7aZRCqwNQRhYGhTkO9NFZyQDU_FUNQ5rV0jcZ0_hYjjB9yd5CGzFGQrPDLEDSX03K8_FR1d3v6g",
    serviceWorkerRegistration: registration
  }).then((currentToken) => {
    if (currentToken) {
      console.log('Token generated:', currentToken);
      // Send the token to your server or use it as needed
    } else {
      console.log('No registration token available. Request permission to generate one.');
    }
  }).catch((err) => {
    console.log('An error occurred while retrieving token. ', err);
  });
};

export const handleForegroundMessages = () => {
  onMessage(messaging, (payload) => {
    console.log('Message received. ', payload);
    // Customize this part to handle foreground messages
  });
};
