import React, { useState } from 'react';
import './Home.css';
import Login from './Login';

function Home() {
  const [isOpen, setIsOpen] = useState(false); // Initial state for sidebar
  const [activeTab, setActiveTab] = useState('home'); // State to track active tab
  const [showLogin, setShowLogin] = useState(false); // State for modal visibility

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const handleTabChange = (tab) => {
    setActiveTab(tab);
    if (tab === 'login') {
      setShowLogin(true); // Show the modal when login tab is clicked
    }
  };

  const handleCloseLogin = () => {
    setShowLogin(false); // Hide the modal
  };

  return (
    <div className="container">
      <aside className={`sidebar ${isOpen ? 'open' : 'closed'}`}>
        <div className="sidebar-header">
          <button className="toggle-btn" onClick={toggleSidebar}>
            <div className="line"></div>
            <div className="line"></div>
            <div className="line"></div>
          </button>
        </div>
        <nav className="nav">
          <ul>
            <li>
              <button
                className={`nav-item ${activeTab === 'home' ? 'active' : ''}`}
                onClick={() => handleTabChange('home')}
              >
                <i className={`bi bi-house icon ${activeTab === 'home' ? 'active' : ''} ${isOpen ? '' : 'no-glow'}`}></i>
                <span className={`text ${!isOpen ? 'hidden' : ''}`}>Home</span>
              </button>
            </li>
            <li>
              <button
                className={`nav-item ${activeTab === 'login' ? 'active' : ''}`}
                onClick={() => handleTabChange('login')}
              >
                <i className={`bi bi-lock icon ${activeTab === 'login' ? 'active' : ''} ${isOpen ? '' : 'no-glow'}`}></i>
                <span className={`text ${!isOpen ? 'hidden' : ''}`}>Login</span>
              </button>
            </li>
            {/* Other nav items */}
          </ul>
        </nav>
      </aside>
      <main className="main-content">
        <header className="header">
          <h1>Welcome to RGUKT Event Management</h1>
        </header>
        <section className="content">
          {activeTab === 'home' && <p>Manage and participate in events seamlessly.</p>}
          {activeTab === 'dashboard' && <p>View your dashboard and stats here.</p>}
          {activeTab === 'event-planning' && <p>Plan your events efficiently.</p>}
          {activeTab === 'event-promotion' && <p>Promote your events to a wider audience.</p>}
          {activeTab === 'volunteer-management' && <p>Manage your volunteers effectively.</p>}
          {activeTab === 'resource-management' && <p>Manage resources for your events.</p>}
          {activeTab === 'feedback-analytics' && <p>Analyze feedback and improve your events.</p>}
        </section>
      </main>
      {/* Render the Login modal */}
      <Login show={showLogin} handleClose={handleCloseLogin} />
    </div>
  );
}

export default Home;
