import React, { useEffect, useState } from 'react';
import './AdminVolunteer.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function EventPromotion() {
  const [eventVolunteerData, setEventVolunteerData] = useState([]);
  const [selectedFiles, setSelectedFiles] = useState({});

  useEffect(() => {
    fetch('http://localhost:5000/api/volunteers/events')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        console.log('Fetched event and volunteer data:', data);
        setEventVolunteerData(data);
      })
      .catch((error) => console.error('Error fetching event and volunteer data:', error));
  }, []);

  const handleFileChange = (event, eventId) => {
    const files = event.target.files;
    setSelectedFiles((prevFiles) => ({
      ...prevFiles,
      [eventId]: files,
    }));
  };

  const handleUpload = (eventId) => {
    const files = selectedFiles[eventId];
    if (!files) return;

    const formData = new FormData();
    for (let i = 0; i < files.length; i++) {
      formData.append('images', files[i]);
    }

    // Assuming your API endpoint for uploading images
    fetch(`http://localhost:5000/api/events/${eventId}/upload-images`, {
      method: 'POST',
      body: formData,
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to upload images');
        }
        return response.json();
      })
      .then((data) => {
        console.log('Images uploaded successfully:', data);
        // Optionally, update UI with the new images
      })
      .catch((error) => console.error('Error uploading images:', error));
  };

  return (
    <div className="container mt-4">
  {eventVolunteerData.map((eventData, index) => (
    // Full-width row for each event
    <div key={index} className="row mb-4">
      <div className="col-12">
        <div className="card" >
          <div className="card-body">
            <h5 className="card-title">{eventData.eventName}</h5>
            <div className="mb-3">
              <label htmlFor={`fileInput-${eventData.id}`} className="form-label">Upload Images</label>
              <input
                type="file"
                id={`fileInput-${eventData.id}`}
                className="form-control"
                multiple
                onChange={(event) => handleFileChange(event, eventData.id)}
              />
              <button
                className="btn btn-primary mt-2"
                onClick={() => handleUpload(eventData.id)}
              >
                Upload
              </button>
            </div>

            {/* Display previously uploaded images */}
            <div className="row">
              {eventData.uploadedImages && eventData.uploadedImages.length > 0 ? (
                eventData.uploadedImages.map((image, imgIndex) => (
                  <div key={imgIndex} className="col-4 mb-3">
                    <div className="card">
                      <img src={image.url} className="card-img-top" alt={image.fileName} />
                      <div className="card-body">
                        <p className="card-text">{image.fileName}</p>
                      </div>
                    </div>
                  </div>
                ))
              ) : (
                <p className="text-muted">No images uploaded for this event.</p>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  ))}
</div>

  );
}

export default EventPromotion;
