import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import SignUp from './SignUp'; // Import your SignUp component

function Login({ show, handleClose }) {
  const [showSignUp, setShowSignUp] = useState(false);

  const handleShowSignUp = () => setShowSignUp(true);
  const handleCloseSignUp = () => setShowSignUp(false);

  const handleSuccess = (response) => {
    console.log('Login Success:', response);
    // Handle successful login (e.g., redirect to dashboard or save user info)
  };

  const handleFailure = (error) => {
    console.log('Login Failed:', error);
    // Handle login failure
  };

  return (
    <>
      <Modal show={show} onHide={handleClose} centered className="login-modal">
        <Modal.Header closeButton>
          <Modal.Title>Login</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form>
            <div className="text-center mb-3">
              <p>Sign in with:</p>
              {/* Removed Google login */}
            </div>
            <p className="text-center">or:</p>
            <div className="form-outline mb-4">
              <Form.Control type="email" id="loginName" />
              <Form.Label htmlFor="loginName">Email or username</Form.Label>
            </div>
            <div className="form-outline mb-4">
              <Form.Control type="password" id="loginPassword" />
              <Form.Label htmlFor="loginPassword">Password</Form.Label>
            </div>
            <div className="row mb-4">
              <div className="col-md-6 d-flex justify-content-center">
                <div className="form-check mb-3 mb-md-0">
                  <Form.Check 
                    type="checkbox" 
                    id="loginCheck" 
                    label="Remember me" 
                    defaultChecked 
                  />
                </div>
              </div>
              <div className="col-md-6 d-flex justify-content-center">
                <a href="#!" onClick={handleShowSignUp}>Register</a>
              </div>
            </div>
            <Button type="submit" className="btn btn-primary btn-block mb-4">Sign in</Button>
            <div className="text-center">
              <p>Not a member? <a href="#!" onClick={handleShowSignUp}>Register</a></p>
            </div>
          </form>
        </Modal.Body>
      </Modal>

      {/* SignUp Modal */}
      <Modal show={showSignUp} onHide={handleCloseSignUp} centered className="sign-up-modal">
        <SignUp show={showSignUp} handleClose={handleCloseSignUp} />
      </Modal>
    </>
  );
}

export default Login;
