import React, { useState } from 'react';
import axios from 'axios';

const FeedbackForm = ({ eventId, userEmail }) => {
  const [rating, setRating] = useState(0);
  const [textFeedback, setTextFeedback] = useState('');

  const handleFeedbackFormSubmit = async (e) => {
    e.preventDefault();
    if (rating < 1 || rating > 5) {
      alert('Rating must be between 1 and 5.');
      return;
    }
    const newFeedback = {
      eventId,
      userEmail,
      rating,
      text: textFeedback,
    };
    try {
      const response = await axios.post('http://localhost:5000/api/feedback/submitFeedback', newFeedback);
      alert('Feedback submitted successfully');
      // Optionally handle success (e.g., close modal, reset form)
    } catch (error) {
      console.error('Error submitting feedback:', error);
      // Optionally handle errors (e.g., show error message)
    }
  };

  return (
    <form onSubmit={handleFeedbackFormSubmit}>
      <div>
        <label>Rating:</label>
        <input
          type="number"
          min="1"
          max="5"
          value={rating}
          onChange={(e) => setRating(e.target.value)}
          required
        />
      </div>
      <div>
        <label>Feedback:</label>
        <textarea
          value={textFeedback}
          onChange={(e) => setTextFeedback(e.target.value)}
          required
        />
      </div>
      <button type="submit">Submit Feedback</button>
    </form>
  );
};

export default FeedbackForm;
