import React, { useState, useEffect } from 'react';
import { Button, Card, Modal, Form, Alert } from 'react-bootstrap';
import axios from 'axios';

const VolunteerManagement = ({ userEmail }) => {
  const [events, setEvents] = useState([]);
  const [showRegisterForm, setShowRegisterForm] = useState(false);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [name, setName] = useState('');
  const [collegeId, setCollegeId] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [registered, setRegistered] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const userData = JSON.parse(localStorage.getItem('userData'));

  useEffect(() => {
    fetchEvents();
  }, []);

  const fetchEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/events/getEvents');
      const eventsWithStatus = response.data.map((event) => ({
        ...event,
        isRegistered: false,
        isClosed: false,
      }));
      setEvents(eventsWithStatus);
    } catch (error) {
      console.error('Error fetching events:', error);
    }
  };

  const handleShowRegisterForm = (event) => {
    setSelectedEvent(event);
    setShowRegisterForm(true);
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    try {
      const newVolunteer = {
        eventId: selectedEvent._id,
        name,
        email: userData[0], // Send email from login details
        collegeId,
        phoneNumber,
        volunteersNeeded: selectedEvent.volunteersNeeded,
      };

      const response = await axios.post('http://localhost:5000/api/volunteers/register', newVolunteer);

      if (response.data.success) {
        setAlertMessage('Registration successful!');
        setRegistered(true);
        setEvents((prevEvents) =>
          prevEvents.map((event) =>
            event._id === selectedEvent._id ? { ...event, isRegistered: true } : event
          )
        );
        setShowRegisterForm(false);
        setName('');
        setCollegeId('');
        setPhoneNumber('');
      } else {
        setAlertMessage(response.data.message);
      }

      fetchEvents();

    } catch (error) {
      console.error('Error registering volunteer:', error);
      setAlertMessage('Registration failed. Please try again.');
    }
  };

  return (
    <div className="container mt-4">
      <div className="event-cards-container">
        {events.map((event) => (
          <Card key={event._id} className="event-card">
            {event.imageUrl && (
              <Card.Img variant="top" src={event.imageUrl} alt={event.eventName} />
            )}
            <Card.Body>
              <Card.Title>{event.eventName}</Card.Title>
              <Button
                variant="primary"
                onClick={() => handleShowRegisterForm(event)}
                disabled={event.isRegistered || event.isClosed}
              >
                {event.isRegistered ? 'Already Registered' : event.isClosed ? 'Closed' : 'Register'}
              </Button>
            </Card.Body>
          </Card>
        ))}
      </div>

      <Modal show={showRegisterForm} onHide={() => setShowRegisterForm(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Register for {selectedEvent?.eventName}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleRegister}>
            <Form.Group controlId="name">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </Form.Group>
            <Form.Group controlId="collegeId">
              <Form.Label>College ID</Form.Label>
              <Form.Control
                type="text"
                value={collegeId}
                onChange={(e) => setCollegeId(e.target.value)}
                required
              />
            </Form.Group>
            <Form.Group controlId="phoneNumber">
              <Form.Label>Phone Number</Form.Label>
              <Form.Control
                type="text"
                value={phoneNumber}
                onChange={(e) => setPhoneNumber(e.target.value)}
                required
              />
            </Form.Group>
            {alertMessage && <Alert variant="info">{alertMessage}</Alert>}
            <Button variant="primary" type="submit" disabled={registered}>
              {registered ? 'Already Registered' : 'Register'}
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default VolunteerManagement;
