import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import axios from 'axios';

function Login({ show, handleClose }) {
  const [isSignUp, setIsSignUp] = useState(false);
  const [userType, setUserType] = useState('');
  const [isVolunteer, setIsVolunteer] = useState(false);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [name, setName] = useState('');
  const [location, setLocation] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleToggle = () => {
    setIsSignUp(!isSignUp);
    setUserType('');
    setIsVolunteer(false);
    setErrorMessage('');
  };

  const handleSubmitLogin = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/loginUser', {
        email,
        password,
      });
      alert('Login Successful');
      console.log('Token:', response.data.authToken);
      handleClose();
    } catch (error) {
      console.error('Login Error:', error);
      setErrorMessage('Login Failed: ' + (error.response?.data?.message || error.message));
    }
  };

  const handleSubmitSignUp = async (event) => {
    event.preventDefault();
    if (password !== confirmPassword) {
      setErrorMessage('Passwords do not match!');
      return;
    }
    try {
      await axios.post('http://localhost:5000/api/createuser', {
        name,
        email,
        password,
        location,
        userType,
        isVolunteer,
      });
      alert('Sign Up Successful');
      handleClose(); 
    } catch (error) {
      console.error('Sign Up Error:', error);
      setErrorMessage('Sign Up Failed: ' + (error.response?.data?.message || error.message));
    }
  };

  return (
    <Modal show={show} onHide={handleClose} centered className="auth-modal">
      <Modal.Header closeButton>
        <Modal.Title>{isSignUp ? 'Sign Up' : 'Login'}</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {errorMessage && (
          <div className="alert alert-danger" role="alert">
            {errorMessage}
          </div>
        )}
        {isSignUp ? (
          <Form onSubmit={handleSubmitSignUp}>
            <Form.Group className="mb-3" controlId="signUpName">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter your name"
                required
                value={name}
                onChange={(e) => setName(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="signUpEmail">
              <Form.Label>Email address</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter your email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="signUpPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Enter your password"
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="signUpConfirmPassword">
              <Form.Label>Confirm Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Confirm your password"
                required
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="signUpLocation">
              <Form.Label>Location</Form.Label>
              <Form.Control
                type="text"
                placeholder="Enter your location"
                required
                value={location}
                onChange={(e) => setLocation(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="userType">
              <Form.Label>Select User Type</Form.Label>
              <Form.Control
                as="select"
                value={userType}
                onChange={(e) => setUserType(e.target.value)}
                required
              >
                <option value="">Select...</option>
                <option value="student">Student</option>
                <option value="faculty">Faculty</option>
                <option value="event-manager">Event Manager</option>
              </Form.Control>
            </Form.Group>
            {userType === 'student' && (
              <Form.Group className="mb-3" controlId="isVolunteer">
                <Form.Check
                  type="checkbox"
                  label="Are you interested in volunteering?"
                  checked={isVolunteer}
                  onChange={(e) => setIsVolunteer(e.target.checked)}
                />
              </Form.Group>
            )}
            <Button variant="primary" type="submit">
              Sign Up
            </Button>
            <Button variant="link" onClick={handleToggle}>
              Already have an account? Login
            </Button>
          </Form>
        ) : (
          <Form onSubmit={handleSubmitLogin}>
            <Form.Group className="mb-3" controlId="loginEmail">
              <Form.Label>Email address</Form.Label>
              <Form.Control
                type="email"
                placeholder="Enter your email"
                required
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </Form.Group>
            <Form.Group className="mb-3" controlId="loginPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                placeholder="Enter your password"
                required
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Login
            </Button>
            <Button variant="link" onClick={handleToggle}>
              New user? Register
            </Button>
          </Form>
        )}
      </Modal.Body>
    </Modal>
  );
}

export default Login;
