//app.js // path src
import React,{useEffect} from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from './components/Home';
import Dashboard from './components/Dashboard';
import EventPlanning from './components/EventPlanning';
import EventPromotion from './components/EventPromotion';
import VolunteerManagement from './components/VolunteerManagement';
import ResourceManagement from './components/ResourceManagement';
import FeedbackAnalytics from './components/FeedbackAnalytics';
import 'bootstrap-icons/font/bootstrap-icons.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { generateToken, handleForegroundMessages} from './firebase';

function App() {
  const [isServiceWorkerReady, setIsServiceWorkerReady] = useState(false);

  useEffect(() => {
    const registerServiceWorker = async () => {
      if ('serviceWorker' in navigator) {
        try {
          const swUrl = `${process.env.PUBLIC_URL}/firebase-messaging-sw.js`;
          const registration = await navigator.serviceWorker.register(swUrl);

          // Check if there's a waiting service worker and activate it
          if (registration.waiting) {
            registration.waiting.postMessage({ type: 'SKIP_WAITING' });
          }

          registration.onupdatefound = () => {
            const installingWorker = registration.installing;
            if (installingWorker) {
              installingWorker.onstatechange = () => {
                if (installingWorker.state === 'installed') {
                  if (navigator.serviceWorker.controller) {
                    console.log('New content is available and will be used when all tabs are closed.');
                  } else {
                    console.log('Content is cached for offline use.');
                  }
                  setIsServiceWorkerReady(true); // Service worker is ready
                }
              };
            }
          };
        } catch (error) {
          console.error('Error during service worker registration:', error);
        }
      } else {
        console.error('Service workers are not supported in this browser.');
      }
    };

    registerServiceWorker();
  }, []);

  useEffect(() => {
    if (isServiceWorkerReady) {
      generateToken();
      handleForegroundMessages();
    }
  }, [isServiceWorkerReady]);

  useEffect(() => {
    reportWebVitals(console.log);
  }, []);

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/event-planning" element={<EventPlanning />} />
          <Route path="/event-promotion" element={<EventPromotion />} />
          <Route path="/volunteer-management" element={<VolunteerManagement />} />
          <Route path="/resource-management" element={<ResourceManagement />} />
          <Route path="/feedback-analytics" element={<FeedbackAnalytics />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
