// src/components/SignUp.js
import React, { useState } from 'react';
import '@fortawesome/fontawesome-free/css/all.min.css';
import { Modal, Button, Form, Alert } from 'react-bootstrap';
import { GoogleLogin } from '@react-oauth/google';
import axios from 'axios';

function SignUp({ show, handleClose, handleShowLogin }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('');
  const [isVolunteer, setIsVolunteer] = useState(false);
  const [message, setMessage] = useState('');
  const [error, setError] = useState('');

  const handleSuccess = (response) => {
    console.log('SignUp Success:', response);
    // Handle successful signup (e.g., redirect to login or dashboard)
  };

  const handleFailure = (error) => {
    console.log('SignUp Failed:', error);
    // Handle signup failure
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('/api/auth/register', {
        email,
        password,
        role,
        isVolunteer,
      });

      setMessage('Registration successful! You can now log in.');
      setError('');
    } catch (err) {
      setError('Registration failed. Please try again.');
      setMessage('');
    }
  };

  return (
    <Modal show={show} onHide={handleClose} centered className="signup-modal">
      <Modal.Header closeButton>
        <Modal.Title>Sign Up</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <form onSubmit={handleSubmit}>
          <div className="text-center mb-3">
            <p>Sign up with:</p>
            <button type="button" className="btn btn-link btn-floating mx-1">
              <GoogleLogin 
                onSuccess={handleSuccess} 
                onFailure={handleFailure}
                style={{ fontSize: '24px' }}
              />
              <i className="fab fa-google"></i>
            </button>
          </div>
          <p className="text-center">or:</p>
          {message && <Alert variant="success">{message}</Alert>}
          {error && <Alert variant="danger">{error}</Alert>}
          <div className="form-outline mb-4">
            <input 
              type="email" 
              id="signupEmail" 
              className="form-control" 
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required 
            />
            <label className="form-label" htmlFor="signupEmail">Email</label>
          </div>
          <div className="form-outline mb-4">
            <input 
              type="password" 
              id="signupPassword" 
              className="form-control" 
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required 
            />
            <label className="form-label" htmlFor="signupPassword">Password</label>
          </div>
          <div className="form-outline mb-4">
            <Form.Group controlId="roleSelect">
              <Form.Label>Select your role</Form.Label>
              <Form.Control as="select" value={role} onChange={(e) => setRole(e.target.value)} required>
                <option value="">Select a role</option>
                <option value="faculty">Faculty</option>
                <option value="event-manager">Event Manager</option>
                <option value="student">Student</option>
              </Form.Control>
            </Form.Group>
          </div>
          {role === 'student' && (
            <div className="form-outline mb-4">
              <Form.Check 
                type="checkbox"
                label="Are you a volunteer?"
                checked={isVolunteer}
                onChange={(e) => setIsVolunteer(e.target.checked)}
              />
            </div>
          )}
          <Button type="submit" className="btn btn-primary btn-block mb-4">Sign Up</Button>
          <div className="text-center">
            <p>Already a member? <a href="#!" onClick={handleShowLogin}>Log In</a></p>
          </div>
        </form>
      </Modal.Body>
    </Modal>
  );
}

export default SignUp;
