import React, { useState } from 'react';
import './Home.css';
import Login from './Login';
import FeedbackAnalytics from './FeedbackAnalytics';
import EventPlanning from './EventPlanning';
import VolunteerManagement from './VolunteerManagement';

function Home() {
  const [showLogin, setShowLogin] = useState(true);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userData, setUserData] = useState(null);

  const handleLoginSuccess = (userData) => {
    setUserData(userData);
    setIsLoggedIn(true);
    setShowLogin(false); // Hide login form after successful login
  };

  const handleLogout = () => {
    localStorage.removeItem('userData');
    localStorage.removeItem('authToken');
    setUserData(null);
    setIsLoggedIn(false);
    setShowLogin(true); // Show login form again after logout
  };

  return (
    <div className="container">
      <div className="intro-section">
        <h1>Welcome to RGUKT Event Management System</h1>
        <p>Manage and participate in events seamlessly.</p>
      </div>
      <div className="login-section">
        {showLogin ? (
          <Login handleLoginSuccess={handleLoginSuccess} />
        ) : (
          <>
            {isLoggedIn ? (
              <div className="main-content">
                <header className="header">
                  <h1>Welcome, {userData.name}!</h1>
                </header>
                <section className="content">
                  <p>Explore your dashboard and manage events.</p>
                  <EventPlanning />
                  {/* Render other components based on user interaction */}
                </section>
                <button className="logout-btn" onClick={handleLogout}>Logout</button>
              </div>
            ) : (
              <Signup toggleLogin={() => setShowLogin(true)} />
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default Home;
