import React, { useState, useEffect } from 'react';
import { Button, Card, Modal, Form } from 'react-bootstrap';
import axios from 'axios';
import './VolunteerManagement.css';

const VolunteerManagement = () => {
  const [completedEvents, setCompletedEvents] = useState([]);
  const [showRegistrationForm, setShowRegistrationForm] = useState(false);
  const [selectedEventId, setSelectedEventId] = useState(null);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [collegeId, setCollegeId] = useState('');
  const [phoneNumber, setPhoneNumber] = useState('');
  const [volunteerAlreadyRegistered, setVolunteerAlreadyRegistered] = useState({});

  useEffect(() => {
    fetchCompletedEvents();
  }, []);

  const fetchCompletedEvents = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/volunteers/completedEvents');
      const { completedEvents } = response.data;

      const userData = JSON.parse(localStorage.getItem('userData'));
      const email = userData[0];

      const eventsWithRegistrationStatus = await Promise.all(
        completedEvents.map(async (event) => {
          try {
            const registrationResponse = await axios.get(`http://localhost:5000/api/volunteers/checkRegistration/${event._id}/${email}`);
            const registrationExists = registrationResponse.data.registrationExists;
            return {
              ...event,
              volunteerAlreadyRegistered: registrationExists,
            };
          } catch (error) {
            console.error(`Error checking registration for event ${event._id}:`, error);
            return {
              ...event,
              volunteerAlreadyRegistered: false,
            };
          }
        })
      );

      setCompletedEvents(eventsWithRegistrationStatus);
    } catch (error) {
      console.error('Error fetching completed events:', error);
    }
  };

  const handleRegister = (eventId) => {
    setSelectedEventId(eventId);
    setShowRegistrationForm(true);
  };

  const handleRegistrationFormSubmit = async (e) => {
    e.preventDefault();
    const newVolunteer = {
      eventId: selectedEventId,
      name,
      email,
      collegeId,
      phoneNumber,
    };
    try {
      await axios.post('http://localhost:5000/api/volunteers/register', newVolunteer);
      alert('Registration successful');
      setShowRegistrationForm(false);
      resetRegistrationForm();
      setVolunteerAlreadyRegistered((prevState) => ({
        ...prevState,
        [selectedEventId]: true,
      }));
      setCompletedEvents((prevEvents) =>
        prevEvents.map((event) =>
          event._id === selectedEventId ? { ...event, volunteerAlreadyRegistered: true } : event
        )
      );
    } catch (error) {
      console.error('Error submitting registration:', error);
    }
  };

  const resetRegistrationForm = () => {
    setName('');
    setEmail('');
    setCollegeId('');
    setPhoneNumber('');
  };

  return (
    <div className="volunteer-container mt-4">
      <div className="volunteer_heading">Completed Events for Volunteer Registration</div>
      <br/>
      <div className="event-cards-container">
        {completedEvents.map((event, index) => (
          <Card key={index} className="event-card">
            {event.imageUrl && (
              <Card.Img variant="top" src={event.imageUrl} alt={event.eventName} className="card-img-top" />
            )}
            <Card.Body className="d-flex flex-column">
              <Card.Title>{event.eventName}</Card.Title>
              {event.volunteerAlreadyRegistered ? (
                <Button variant="success" disabled>
                  Already Registered
                </Button>
              ) : (
                <Button variant="primary" onClick={() => handleRegister(event._id)}>
                  Register
                </Button>
              )}
            </Card.Body>
          </Card>
        ))}
      </div>

      <Modal show={showRegistrationForm} onHide={() => setShowRegistrationForm(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Register as Volunteer</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleRegistrationFormSubmit}>
            <Form.Group controlId="name">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                value={name}
                onChange={(e) => setName(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="email">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="collegeId">
              <Form.Label>College ID</Form.Label>
              <Form.Control
                type="text"
                value={collegeId}
                onChange={(e) => setCollegeId(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="phoneNumber">
              <Form.Label>Phone Number</Form.Label>
              <Form.Control
                type="text"
                value={phoneNumber}
                onChange={(e) => setPhoneNumber(e.target.value)}
                required
              />
            </Form.Group>

            <Button variant="primary" type="submit">
              Submit
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default VolunteerManagement;
