import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Card } from 'react-bootstrap';

const EventPlanning = () => {
  const [name, setName] = useState('');
  const [date, setDate] = useState('');
  const [tasks, setTasks] = useState([{ detail: '', assignee: '', deadline: '' }]);
  const [venue, setVenue] = useState('');
  const [events, setEvents] = useState([]);
  const [showForm, setShowForm] = useState(false); // Add showForm state

  // Fetch initial list of events
  useEffect(() => {
    const fetchEvents = async () => {
      const response = await axios.get('http://localhost:5000/events');
      setEvents(response.data);
    };
    fetchEvents();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newEvent = { name, date, tasks, venue };
    const response = await axios.post('http://localhost:5000/events', newEvent);
    setEvents([...events, response.data]);
    setName('');
    setDate('');
    setTasks([{ detail: '', assignee: '', deadline: '' }]);
    setVenue('');
    setShowForm(false); // Hide form after submission
  };

  const handleTaskChange = (index, field, value) => {
    const updatedTasks = [...tasks];
    updatedTasks[index] = { ...updatedTasks[index], [field]: value };
    setTasks(updatedTasks);
  };

  const addTaskField = () => {
    setTasks([...tasks, { detail: '', assignee: '', deadline: '' }]);
  };

  return (
    <div>
      <Button variant="primary" onClick={() => setShowForm(!showForm)}>
        + Add Event
      </Button>
      {showForm && (
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="eventName">
            <Form.Label>Event Name</Form.Label>
            <Form.Control
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </Form.Group>

          <Form.Group controlId="eventDate">
            <Form.Label>Date</Form.Label>
            <Form.Control
              type="date"
              value={date}
              onChange={(e) => setDate(e.target.value)}
              required
            />
          </Form.Group>

          {tasks.map((task, index) => (
            <div key={index} className="mb-3">
              <Form.Group controlId={`taskDetail-${index}`}>
                <Form.Label>Task Detail</Form.Label>
                <Form.Control
                  type="text"
                  value={task.detail}
                  onChange={(e) => handleTaskChange(index, 'detail', e.target.value)}
                  required
                />
              </Form.Group>
              <Form.Group controlId={`taskAssignee-${index}`}>
                <Form.Label>Assignee</Form.Label>
                <Form.Control
                  type="text"
                  value={task.assignee}
                  onChange={(e) => handleTaskChange(index, 'assignee', e.target.value)}
                  required
                />
              </Form.Group>
              <Form.Group controlId={`taskDeadline-${index}`}>
                <Form.Label>Deadline</Form.Label>
                <Form.Control
                  type="date"
                  value={task.deadline}
                  onChange={(e) => handleTaskChange(index, 'deadline', e.target.value)}
                  required
                />
              </Form.Group>
            </div>
          ))}

          <Button variant="secondary" onClick={addTaskField}>
            Add Task
          </Button>

          <Form.Group controlId="eventVenue">
            <Form.Label>Venue</Form.Label>
            <Form.Control
              type="text"
              value={venue}
              onChange={(e) => setVenue(e.target.value)}
              required
            />
          </Form.Group>

          <Button variant="primary" type="submit">
            Create Event
          </Button>
        </Form>
      )}

      <div className="mt-4">
        {events.map((event) => (
          <Card key={event._id} className="mb-3">
            <Card.Body>
              <Card.Title>{event.name}</Card.Title>
              <Card.Subtitle className="mb-2 text-muted">{event.date}</Card.Subtitle>
              <Card.Text>
                Venue: {event.venue}
                <br />
                Tasks:
                <ul>
                  {event.tasks.map((task, index) => (
                    <li key={index}>
                      {task.detail} - {task.assignee} (Deadline: {task.deadline})
                    </li>
                  ))}
                </ul>
              </Card.Text>
            </Card.Body>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default EventPlanning;
