import React, { useState, useEffect } from 'react';
import axios from 'axios';

function EventForm() {
  const [eventName, setEventName] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [venue, setVenue] = useState('');
  const [imageUrl, setImageUrl] = useState('');
  const [events, setEvents] = useState([]);

  useEffect(() => {
    if (Notification.permission === 'default') {
      Notification.requestPermission().then(permission => {
        if (permission === 'granted') {
          registerDeviceToken();
        }
      });
    } else if (Notification.permission === 'granted') {
      registerDeviceToken();
    }
  }, []);

  const registerDeviceToken = () => {
    // Assuming deviceToken is obtained from your notification service
    const deviceToken = localStorage.getItem('deviceToken');
    if (deviceToken) {
      axios.post('http://localhost:5000/api/updateDeviceToken', {
        email: JSON.parse(localStorage.getItem('userData'))[0], // Assuming email is stored in local storage
        deviceToken
      }).catch(error => console.error('Error registering device token:', error));
    }
  };

  const handleEventFormSubmit = async (e) => {
    e.preventDefault();
    const today = new Date().setHours(0, 0, 0, 0); // Reset the time to 00:00:00 for comparison
    if (new Date(startDate).getTime() <= today) {
      alert('Start date should be after today\'s date.');
      return;
    }

    if (new Date(endDate).getTime() < new Date(startDate).getTime()) {
      alert('End date should be on or after the start date.');
      return;
    }

    const newEvent = {
      eventName,
      startDate,
      endDate,
      venue,
      imageUrl,
      eventPrograms: []
    };

    try {
      const response = await axios.post('http://localhost:5000/api/events/createEvent', newEvent);
      setEvents([...events, response.data.event]);
      resetEventForm();
      showNotification('New Event Created', `Event "${eventName}" has been successfully created. Register now!`);
    } catch (error) {
      console.error('Error creating event:', error);
    }
  };

  const showNotification = (title, body) => {
    if (Notification.permission === 'granted') {
      new Notification(title, { body });
    }
  };

  const resetEventForm = () => {
    setEventName('');
    setStartDate('');
    setEndDate('');
    setVenue('');
    setImageUrl('');
  };

  return (
    <form onSubmit={handleEventFormSubmit}>
      <div>
        <label>Event Name:</label>
        <input type="text" value={eventName} onChange={(e) => setEventName(e.target.value)} required />
      </div>
      <div>
        <label>Start Date:</label>
        <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} required />
      </div>
      <div>
        <label>End Date:</label>
        <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} required />
      </div>
      <div>
        <label>Venue:</label>
        <input type="text" value={venue} onChange={(e) => setVenue(e.target.value)} required />
      </div>
      <div>
        <label>Image URL:</label>
        <input type="text" value={imageUrl} onChange={(e) => setImageUrl(e.target.value)} />
      </div>
      <button type="submit">Create Event</button>
    </form>
  );
}

export default EventForm;
