import React, { useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from './components/Home';
import Dashboard from './components/Dashboard';
import EventPlanning from './components/EventPlanning';
import EventPromotion from './components/EventPromotion';
import VolunteerManagement from './components/VolunteerManagement';
import ResourceManagement from './components/ResourceManagement';
import FeedbackAnalytics from './components/FeedbackAnalytics';
import 'bootstrap-icons/font/bootstrap-icons.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { getToken, setupNotifications, checkNotificationPermission } from './firebase';

// Register service worker and set up notifications
const registerServiceWorker = async () => {
    if ('serviceWorker' in navigator) {
        try {
            const registration = await navigator.serviceWorker.register('/firebase-messaging-sw.js');
            console.log('Service Worker registered with scope:', registration.scope);

            // Initialize notifications
            await getToken();
            setupNotifications((payload) => {
                console.log('Foreground message received:', payload);
                // Handle foreground notifications here
                const { title, body } = payload.notification;
                // Custom notification handler
                new Notification(title, { body });
            });
        } catch (error) {
            console.error('Service Worker registration failed:', error);
        }
    }
};

function App() {
    useEffect(() => {
        // Register service worker and set up notifications
        registerServiceWorker();
    }, []);

    useEffect(() => {
        const checkPermission = async () => {
            const permission = await checkNotificationPermission();
            console.log('Notification permission:', permission);
        };

        checkPermission();
    }, []);

    return (
        <Router>
            <div className="App">
                <Routes>
                    <Route path="/" element={<Home />} />
                    <Route path="/dashboard" element={<Dashboard />} />
                    <Route path="/event-planning" element={<EventPlanning />} />
                    <Route path="/event-promotion" element={<EventPromotion />} />
                    <Route path="/volunteer-management" element={<VolunteerManagement />} />
                    <Route path="/resource-management" element={<ResourceManagement />} />
                    <Route path="/feedback-analytics" element={<FeedbackAnalytics />} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
