import React, { useState } from 'react';
import { Form, Button, Card, Modal, Row, Col } from 'react-bootstrap';
import './EventPlanning.css';

const EventPlanning = () => {
  const [name, setName] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [venue, setVenue] = useState('');
  const [events, setEvents] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [showProgramModal, setShowProgramModal] = useState(false);
  const [currentEventIndex, setCurrentEventIndex] = useState(null);
  const [programName, setProgramName] = useState('');
  const [programStartDate, setProgramStartDate] = useState('');
  const [programStartTime, setProgramStartTime] = useState('');
  const [programEndTime, setProgramEndTime] = useState('');
  const [programVenue, setProgramVenue] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const newEvent = { name, startDate, endDate, venue, eventPrograms: [] };
    setEvents([...events, newEvent]);
    setName('');
    setStartDate('');
    setEndDate('');
    setVenue('');
    setShowForm(false);
  };

  const deleteEvent = (index) => {
    const updatedEvents = events.filter((_, i) => i !== index);
    setEvents(updatedEvents);
  };

  const addOrEditProgram = (e) => {
    e.preventDefault();
    const updatedEvents = [...events];
    if (currentEventIndex !== null) {
      const eventPrograms = updatedEvents[currentEventIndex].eventPrograms;
      eventPrograms.push({
        name: programName,
        startDate: programStartDate,
        startTime: programStartTime,
        endTime: programEndTime,
        venue: programVenue,
      });
      updatedEvents[currentEventIndex].eventPrograms = eventPrograms;
      setEvents(updatedEvents);
    }
    setShowProgramModal(false);
    setProgramName('');
    setProgramStartDate('');
    setProgramStartTime('');
    setProgramEndTime('');
    setProgramVenue('');
    setCurrentEventIndex(null);
  };

  const openProgramModal = (index) => {
    setCurrentEventIndex(index);
    setShowProgramModal(true);
  };

  return (
    <div className="container">
      <Button variant="primary" onClick={() => setShowForm(!showForm)}>
        + Add Event
      </Button>
      {showForm && (
        <Form onSubmit={handleSubmit}>
          <Form.Group controlId="eventName">
            <Form.Label>Event Name</Form.Label>
            <Form.Control
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
              required
            />
          </Form.Group>

          <Form.Group controlId="eventStartDate">
            <Form.Label>Start Date</Form.Label>
            <Form.Control
              type="date"
              value={startDate}
              onChange={(e) => setStartDate(e.target.value)}
              required
            />
          </Form.Group>

          <Form.Group controlId="eventEndDate">
            <Form.Label>End Date</Form.Label>
            <Form.Control
              type="date"
              value={endDate}
              onChange={(e) => setEndDate(e.target.value)}
              required
            />
          </Form.Group>

          <Form.Group controlId="eventVenue">
            <Form.Label>Venue</Form.Label>
            <Form.Control
              type="text"
              value={venue}
              onChange={(e) => setVenue(e.target.value)}
              required
            />
          </Form.Group>

          <Button variant="primary" type="submit">
            Create Event
          </Button>
        </Form>
      )}

      <Row className="mt-4">
      <Button
                    variant="info"
                    size="sm"
                    onClick={() => openProgramModal(eventIndex)}
                  >
                    + Add Event Program
                  </Button>
        {events.map((event, eventIndex) => (
          <Col key={eventIndex} lg={3} md={6} sm={12} className="mb-4">
            <Card className="square-card">
              <Card.Body className="d-flex flex-column">
                <Card.Title className="mb-auto">{event.name}</Card.Title>
                <img
                  className="card-image"
                  src="https://via.placeholder.com/150"
                  alt="Event"
                />
                <Card.Text className="mt-3">
                  <strong>Date:</strong> {event.startDate} to {event.endDate}
                </Card.Text>
                <Card.Text>
                  <strong>Venue:</strong> {event.venue}
                </Card.Text>
                <div className="mt-auto d-flex justify-content-between align-items-center">

                  <Button
                    variant="danger"
                    size="sm"
                    onClick={() => deleteEvent(eventIndex)}
                  >
                    <i className="bi bi-trash" />
                  </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>

      <Modal show={showProgramModal} onHide={() => setShowProgramModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add Event Program</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={addOrEditProgram}>
            <Form.Group controlId="programName">
              <Form.Label>Program Name</Form.Label>
              <Form.Control
                type="text"
                value={programName}
                onChange={(e) => setProgramName(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="programStartDate">
              <Form.Label>Date</Form.Label>
              <Form.Control
                type="date"
                value={programStartDate}
                onChange={(e) => setProgramStartDate(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="programStartTime">
              <Form.Label>Start Time</Form.Label>
              <Form.Control
                type="time"
                value={programStartTime}
                onChange={(e) => setProgramStartTime(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="programEndTime">
              <Form.Label>End Time</Form.Label>
              <Form.Control
                type="time"
                value={programEndTime}
                onChange={(e) => setProgramEndTime(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="programVenue">
              <Form.Label>Venue</Form.Label>
              <Form.Control
                type="text"
                value={programVenue}
                onChange={(e) => setProgramVenue(e.target.value)}
                required
              />
            </Form.Group>

            <Button variant="primary" type="submit">
              Save Program
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default EventPlanning;
