import React, { useState, useEffect } from 'react';
import './Home.css';
import Login from './Login';
import FeedbackAnalytics from './FeedbackAnalytics';
import EventPlanning from './EventPlanning';
import VolunteerManagement from './VolunteerManagement';
import AdminVolunteer from './AdminVolunteer'; // Import the AdminVolunteer component
import { Modal, Button } from 'react-bootstrap';

function Home() {
  const [isOpen, setIsOpen] = useState(false);
  const [activeTab, setActiveTab] = useState('home');
  const [showLogin, setShowLogin] = useState(false);
  const [userData, setUserData] = useState(null);

  useEffect(() => {
    const user = JSON.parse(localStorage.getItem('userData'));
    if (user) {
      setUserData({
        email: user[0],
        name: user[1],
        userType: user[2],
        mobileNumber: user[3],
      });
    }
  }, []);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const handleTabChange = (tab) => {
    if (tab === 'home') {
      setActiveTab('home');
    } else if (tab === 'login' && userData) {
      setShowLogin(false);
      setActiveTab(tab);
    } else if (userData) {
      setShowLogin(false);
      setActiveTab(tab);
    } else {
      setShowLogin(true);
      setActiveTab('login');
    }
  };

  const handleCloseLogin = () => {
    setShowLogin(false);
    const user = JSON.parse(localStorage.getItem('userData'));
    if (user) {
      setUserData({
        email: user[0],
        name: user[1],
        userType: user[2],
        mobileNumber: user[3],
      });
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('userData');
    localStorage.removeItem('authToken');
    setUserData(null);
    setShowProfile(false);
    setActiveTab('home');
  };

  return (
    <div className="container">
      <aside className={`sidebar ${isOpen ? 'open' : 'closed'}`}>
        {/* Sidebar code */}
      </aside>
      <main className={`main-content ${!isOpen ? '' : 'closed'}`}>
        <header className="header">
          <h1>RKV Event Management System</h1>
        </header>
        <section className="content">
          {activeTab === 'home' && <p>Manage and participate in events seamlessly.</p>}
          {activeTab === 'login' && userData && (
            <div>
              <p>Name: {userData.name}</p>
              <p>Email: {userData.email}</p>
              <p>Mobile Number: {userData.mobileNumber}</p>
              <p>User Type: {userData.userType}</p>
              <Button variant="danger" onClick={handleLogout}>Logout</Button>
            </div>
          )}
          {activeTab === 'login' && !userData && <p>Please login to access more features.</p>}
          {activeTab === 'dashboard' && <p>View your dashboard and stats here.</p>}
          {activeTab === 'event-planning' && <EventPlanning />}
          {activeTab === 'event-promotion' && <p>Promote your events to a wider audience.</p>}
          {/* Conditional rendering based on user type */}
          {activeTab === 'volunteer-management' && (
            userData?.userType === 'student' ? (
              <VolunteerManagement />
            ) : (
              <AdminVolunteer />
            )
          )}
          {activeTab === 'resource-management' && <p>Manage resources for your events.</p>}
          {activeTab === 'feedback-analytics' && <FeedbackAnalytics />}
        </section>
      </main>
      <Login show={showLogin} handleClose={handleCloseLogin} />
    </div>
  );
}

export default Home;
