import React, { useState } from 'react';
import './Home.css';
import Login from './Login';
import FeedbackAnalytics from './FeedbackAnalytics';
import EventTimeline from './EventTimeline.css'; // Import the EventTimeline component

function Home() {
  const [isOpen, setIsOpen] = useState(false);
  const [activeTab, setActiveTab] = useState('home');
  const [showLogin, setShowLogin] = useState(false);
  const [showSignUp, setShowSignUp] = useState(false);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const handleTabChange = (tab) => {
    setActiveTab(tab);
    if (tab === 'login') {
      setShowLogin(true);
    }
  };

  const handleCloseLogin = () => {
    setShowLogin(false);
  };

  const handleShowSignUp = () => {
    setShowLogin(false);
    setShowSignUp(true);
  };

  const handleCloseSignUp = () => {
    setShowSignUp(false);
  };

  return (
    <div className="container">
      <aside className={`sidebar ${isOpen ? 'open' : 'closed'}`}>
        <div className="sidebar-header">
          <button className="toggle-btn" onClick={toggleSidebar}>
            <div className="line"></div>
            <div className="line"></div>
            <div className="line"></div>
          </button>
        </div>
        <nav className="nav">
          <ul>
            {/* Your sidebar items here */}
          </ul>
        </nav>
      </aside>
      <main className="main-content">
        <header className="header">
          <h1>Welcome to RGUKT Event Management System</h1>
        </header>
        <section className="content">
          {activeTab === 'home' && <p>Manage and participate in events seamlessly.</p>}
          {activeTab === 'login' && <p>Please login to access more features.</p>}
          {activeTab === 'dashboard' && <p>View your dashboard and stats here.</p>}
          {activeTab === 'event-planning' && <p>Plan your events efficiently.</p>}
          {activeTab === 'event-promotion' && <p>Promote your events to a wider audience.</p>}
          {activeTab === 'volunteer-management' && <p>Manage your volunteers effectively.</p>}
          {activeTab === 'resource-management' && <p>Manage resources for your events.</p>}
          {activeTab === 'feedback-analytics' && (
            <>
              <FeedbackAnalytics />
              <EventTimeline />
            </>
          )}
        </section>
      </main>
      <Login show={showLogin} handleClose={handleCloseLogin} handleShowSignUp={handleShowSignUp} />
    </div>
  );
}

export default Home;
