import React,{useEffect} from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import Home from './components/Home';
import Dashboard from './components/Dashboard';
import EventPlanning from './components/EventPlanning';
import EventPromotion from './components/EventPromotion';
import VolunteerManagement from './components/VolunteerManagement';
import ResourceManagement from './components/ResourceManagement';
import FeedbackAnalytics from './components/FeedbackAnalytics';
import 'bootstrap-icons/font/bootstrap-icons.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { messaging ,getToken} from './firebase';
import axios from 'axios';

function App() {
  useEffect(() => {
    const requestPermission = async () => {
      try {
        // Request permission to show notifications
        const permission = await Notification.requestPermission();
        if (permission === "granted") {
          console.log('Notification permission granted.');

          // Get the FCM token
          const token = await getToken(messaging, { vapidKey: 'BF9Q0ytyvFzr7aZRCqwNQRhYGhTkO9NFZyQDU_FUNQ5rV0jcZ0_hYjjB9yd5CGzFGQrPDLEDSX03K8_FR1d3v6g' }); // Add your VAPID key here
          if (token) {
            // Send the token to your server to save
            await axios.post('http://localhost:5000/api/saveToken', { token });
          }
        } else {
          console.log('Notification permission denied.');
        }
      } catch (error) {
        console.error('Error getting permission or token:', error);
      }
    };

    requestPermission();
  }, []);

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/dashboard" element={<Dashboard />} />
          <Route path="/event-planning" element={<EventPlanning />} />
          <Route path="/event-promotion" element={<EventPromotion />} />
          <Route path="/volunteer-management" element={<VolunteerManagement />} />
          <Route path="/resource-management" element={<ResourceManagement />} />
          <Route path="/feedback-analytics" element={<FeedbackAnalytics />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
