import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './EventPromotion.css'; // Ensure to create and include this CSS file

const EventPromotion = () => {
  const [eventName, setEventName] = useState('');
  const [selectedFiles, setSelectedFiles] = useState([]);
  const [images, setImages] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const user = JSON.parse(localStorage.getItem('userData'));
  const userType = user ? user[2] : '';

  useEffect(() => {
    if (userType === 'event-manager' && eventName) {
      fetchImages();
    }
  }, [userType, eventName]);

  const fetchImages = async () => {
    try {
      const response = await axios.get(`/api/promotions/${eventName}`);
      setImages(response.data.images || []);
    } catch (error) {
      console.error('Error fetching images:', error);
    }
  };

  const handleFileChange = (e) => {
    setSelectedFiles([...e.target.files]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('eventName', eventName);
    for (const file of selectedFiles) {
      formData.append('images', file);
    }

    try {
      await axios.post('/api/promotions/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      fetchImages();
      setSelectedFiles([]);
    } catch (error) {
      console.error('Error uploading images:', error);
    }
  };

  const handleDelete = async (imageId) => {
    try {
      await axios.delete(`/api/promotions/delete/${imageId}`);
      fetchImages();
    } catch (error) {
      console.error('Error deleting image:', error);
    }
  };

  return (
    <div className="promotion-images">
      <h1>Promotion Images</h1>
      {userType === 'event-manager' && (
        <div>
          <button
            className="btn-toggle"
            onClick={() => setShowForm(!showForm)}
          >
            {showForm ? 'Cancel' : 'Promote Event'}
          </button>
          {showForm && (
            <div className="form-container">
              <input
                type="text"
                placeholder="Event Name"
                value={eventName}
                onChange={(e) => setEventName(e.target.value)}
              />
              <input
                type="file"
                multiple
                onChange={handleFileChange}
              />
              <button onClick={handleUpload}>Upload Images</button>
            </div>
          )}
        </div>
      )}
      <div className="image-gallery">
        {images.map((img) => (
          <div key={img._id} className="image-item">
            <img src={img.url} alt="" className="image-preview" />
            {userType === 'event-manager' && (
              <button onClick={() => handleDelete(img._id)}>Delete</button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default EventPromotion;
