// firebase.js //path src
import { initializeApp } from "firebase/app";
import { getMessaging, getToken, onMessage } from 'firebase/messaging';// https://firebase.google.com/docs/web/setup#available-libraries
import axios from 'axios';
// Your web app's Firebase configuration
// For Firebase JS SDK v7.20.0 and later, measurementId is optional
const firebaseConfig = {
  apiKey: "AIzaSyBB4VCDW8XtHsMs-Smh3CyhQC4GBeNqUOw",
  authDomain: "event-management-8c4d4.firebaseapp.com",
  projectId: "event-management-8c4d4",
  storageBucket: "event-management-8c4d4.appspot.com",
  messagingSenderId: "763831488241",
  appId: "1:763831488241:web:393a46c889d7910f6aa350",
  measurementId: "G-L5RD59TYLT"
};

const generateToken = async () => {
  try {
    const existingToken = localStorage.getItem('fcmToken');
    if (existingToken) {
      console.log('Token already exists:', existingToken);
      return existingToken;
    }

    const permission = await Notification.requestPermission();
    if (permission === "granted") {
      const token = await getToken(messaging, {
        vapidKey: "BF9Q0ytyvFzr7aZRCqwNQRhYGhTkO9NFZyQDU_FUNQ5rV0jcZ0_hYjjB9yd5CGzFGQrPDLEDSX03K8_FR1d3v6g"
      });
      console.log('New token generated:', token);
      localStorage.setItem('fcmToken', token);
      return token;
    } else {
      console.log('Permission not granted for notifications');
    }
  } catch (error) {
    console.error('Error generating token:', error);
  }
};

// Initialize Firebase
const app = initializeApp(firebaseConfig);
const messaging = getMessaging(app);
export { messaging, getToken, onMessage, generateToken };
