import React, { useState } from 'react';
import './Home.css';
import Login from './Login';
import FeedbackAnalytics from './FeedbackAnalytics';
import EventPlanning from './EventPlanning';
import VolunteerManagement from './VolunteerManagement';

function Home() {
  const [isOpen, setIsOpen] = useState(false);
  const [activeTab, setActiveTab] = useState('home');
  const [showLogin, setShowLogin] = useState(false);
  const [showSignUp, setShowSignUp] = useState(false);

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const handleTabChange = (tab) => {
    setActiveTab(tab);
    if (tab === 'login') {
      setShowLogin(true);
    } else {
      setShowLogin(false);
    }
  };

  const handleCloseLogin = () => {
    setShowLogin(false);
  };

  const handleShowSignUp = () => {
    setShowLogin(false);
    setShowSignUp(true);
  };

  const handleCloseSignUp = () => {
    setShowSignUp(false);
  };

  return (
    <div className="container">
      <aside className={`sidebar ${isOpen ? '' : 'closed'}`}>
        <div className="sidebar-header">
          <button className="toggle-btn" onClick={toggleSidebar}>
            <div className="line"></div>
            <div className="line"></div>
            <div className="line"></div>
          </button>
        </div>
        <nav className="nav">
          <ul>
            <li>
              <button
                className={`nav-item ${activeTab === 'home' ? 'active' : ''}`}
                onClick={() => handleTabChange('home')}
              >
                <i className={`bi bi-house icon ${activeTab === 'home' ? 'active' : ''}`}></i>
                <span className={`text ${isOpen ? '' : 'hidden'}`}>Home</span>
              </button>
            </li>
            <li>
              <button
                className={`nav-item ${activeTab === 'login' ? 'active' : ''}`}
                onClick={() => handleTabChange('login')}
              >
                <i className={`bi bi-lock icon ${activeTab === 'login' ? 'active' : ''}`}></i>
                <span className={`text ${isOpen ? '' : 'hidden'}`}>Login</span>
              </button>
            </li>
            <li>
              <button
                className={`nav-item ${activeTab === 'dashboard' ? 'active' : ''}`}
                onClick={() => handleTabChange('dashboard')}
              >
                <i className={`bi bi-bar-chart icon ${activeTab === 'dashboard' ? 'active' : ''}`}></i>
                <span className={`text ${isOpen ? '' : 'hidden'}`}>Dashboard</span>
              </button>
            </li>
            <li>
              <button
                className={`nav-item ${activeTab === 'event-planning' ? 'active' : ''}`}
                onClick={() => handleTabChange('event-planning')}
              >
                <i className={`bi bi-calendar icon ${activeTab === 'event-planning' ? 'active' : ''}`}></i>
                <span className={`text ${isOpen ? '' : 'hidden'}`}>Event Planning</span>
              </button>
            </li>
            <li>
              <button
                className={`nav-item ${activeTab === 'event-promotion' ? 'active' : ''}`}
                onClick={() => handleTabChange('event-promotion')}
              >
                <i className={`bi bi-megaphone icon ${activeTab === 'event-promotion' ? 'active' : ''}`}></i>
                <span className={`text ${isOpen ? '' : 'hidden'}`}>Event Promotion</span>
              </button>
            </li>
            <li>
              <button
                className={`nav-item ${activeTab === 'volunteer-management' ? 'active' : ''}`}
                onClick={() => handleTabChange('volunteer-management')}
              >
                <i className={`bi bi-person-badge icon ${activeTab === 'volunteer-management' ? 'active' : ''}`}></i>
                <span className={`text ${isOpen ? '' : 'hidden'}`}>Volunteer Management</span>
              </button>
            </li>
            <li>
              <button
                className={`nav-item ${activeTab === 'resource-management' ? 'active' : ''}`}
                onClick={() => handleTabChange('resource-management')}
              >
                <i className={`bi bi-tools icon ${activeTab === 'resource-management' ? 'active' : ''}`}></i>
                <span className={`text ${isOpen ? '' : 'hidden'}`}>Resource Management</span>
              </button>
            </li>
            <li>
              <button
                className={`nav-item ${activeTab === 'feedback-analytics' ? 'active' : ''}`}
                onClick={() => handleTabChange('feedback-analytics')}
              >
                <i className={`bi bi-graph-up icon ${activeTab === 'feedback-analytics' ? 'active' : ''}`}></i>
                <span className={`text ${isOpen ? '' : 'hidden'}`}>Feedback & Analytics</span>
              </button>
            </li>
          </ul>
        </nav>
      </aside>
      <main className="main-content">
        <header className="header">
          <h1>Welcome to RGUKT Event Management System</h1>
        </header>
        <section className="content">
          {activeTab === 'home' && <p>Manage and participate in events efficiently with our event management system.</p>}
          {activeTab === 'login' && showLogin && <Login onClose={handleCloseLogin} onSignUp={handleShowSignUp} />}
          {activeTab === 'feedback-analytics' && <FeedbackAnalytics />}
          {activeTab === 'event-planning' && <EventPlanning />}
          {activeTab === 'volunteer-management' && <VolunteerManagement />}
        </section>
      </main>
    </div>
  );
}

export default Home;
