import React from 'react';
import { Modal, Button,Form,role } from 'react-bootstrap';

function SignUp({ show, handleClose }) {
  const handleSuccess = (response) => {
    console.log('Sign Up Success:', response);
    // Handle successful sign-up (e.g., redirect to dashboard or save user info)
  };

  const handleFailure = (error) => {
    console.log('Sign Up Failed:', error);
    // Handle sign-up failure
  };

  return (
    <Modal show={show} onHide={handleClose} centered className="sign-up-modal">
      <Modal.Header closeButton>
        <Modal.Title>Sign Up</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <form>
          <p className="text-center mb-3">Create an account:</p>
          <div className="form-outline mb-4">
            <input type="email" id="signUpEmail" className="form-control" />
            <label className="form-label" htmlFor="signUpEmail">Email</label>
          </div>
          <div className="form-outline mb-4">
            <input type="password" id="signUpPassword" className="form-control" />
            <label className="form-label" htmlFor="signUpPassword">Password</label>
          </div>
          <div className="form-outline mb-4">
            <input type="password" id="signUpConfirmPassword" className="form-control" />
            <label className="form-label" htmlFor="signUpConfirmPassword">Confirm Password</label>
          </div>
          <div className="form-outline mb-4">
            <input type="text" id="signUpName" className="form-control" />
            <label className="form-label" htmlFor="signUpName">Full Name</label>
          </div>
          <div className="mb-4">
            <Form.Group controlId="roleSelect">
              <Form.Label>Select your role</Form.Label>
              <Form.Control as="select">
                <option value="">Select a role</option>
                <option value="faculty">Faculty</option>
                <option value="event-manager">Event Manager</option>
                <option value="student">Student</option>
              </Form.Control>
            </Form.Group>
          </div>
          {role === 'student' && (
            <div className="mb-4">
              <Form.Group controlId="volunteerCheck">
                <Form.Check 
                  type="checkbox"
                  label="Are you a volunteer?"
                />
              </Form.Group>
            </div>
          )}
          <Button type="submit" className="btn btn-primary btn-block mb-4">Sign Up</Button>
          <div className="text-center">
            <p>Already a member? <a href="#!" onClick={handleClose}>Login</a></p>
          </div>
        </form>
      </Modal.Body>
    </Modal>
  );
}

export default SignUp;
