import React, { useState, useEffect } from 'react';
import { Button, Card, Modal, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';
import './FeedbackAnalytics.css';

const FeedbackAnalytics = () => {
  const [feedbacks, setFeedbacks] = useState([]);
  const [showFeedbackForm, setShowFeedbackForm] = useState(false);
  const [rating, setRating] = useState(0);
  const [textFeedback, setTextFeedback] = useState('');

  useEffect(() => {
    fetchFeedbacks();
  }, []);

  const fetchFeedbacks = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/feedback/getFeedbacks');
      setFeedbacks(response.data);
    } catch (error) {
      console.error('Error fetching feedbacks:', error);
    }
  };

  const handleAddFeedback = () => {
    setShowFeedbackForm(true);
  };

  const handleFeedbackFormSubmit = async (e) => {
    e.preventDefault();
    if (rating < 1 || rating > 5) {
      alert('Rating must be between 1 and 5.');
      return;
    }
    const newFeedback = {
      rating,
      textFeedback,
    };
    try {
      const response = await axios.post('http://localhost:5000/api/feedback/createFeedback', newFeedback);
      setFeedbacks([...feedbacks, response.data.feedback]);
      resetFeedbackForm();
    } catch (error) {
      console.error('Error creating feedback:', error);
    }
  };

  const resetFeedbackForm = () => {
    setRating(0);
    setTextFeedback('');
    setShowFeedbackForm(false);
  };

  return (
    <div className="container mt-4">
      <Button variant="primary" onClick={handleAddFeedback} className="mb-3 fixed-add-feedback-btn">
        + Add Feedback
      </Button>

      <Modal show={showFeedbackForm} onHide={() => setShowFeedbackForm(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add Feedback</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleFeedbackFormSubmit}>
            <Form.Group controlId="rating">
              <Form.Label>Rating (1-5)</Form.Label>
              <Form.Control
                type="number"
                min="1"
                max="5"
                value={rating}
                onChange={(e) => setRating(parseInt(e.target.value))}
                required
              />
            </Form.Group>

            <Form.Group controlId="textFeedback">
              <Form.Label>Text Feedback (Max 30 characters)</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                maxLength={30}
                value={textFeedback}
                onChange={(e) => setTextFeedback(e.target.value)}
                required
              />
            </Form.Group>

            <Button variant="primary" type="submit">
              Add Feedback
            </Button>
          </Form>
        </Modal.Body>
      </Modal>

      <div className="feedback-cards-container">
        {feedbacks.map((feedback, index) => (
          <Card key={index} className="feedback-card">
            <Card.Body>
              <Card.Title>Rating: {feedback.rating}</Card.Title>
              <Card.Text>
                <strong>Feedback:</strong> {feedback.textFeedback}
              </Card.Text>
            </Card.Body>
          </Card>
        ))}
      </div>
    </div>
  );
};

export default FeedbackAnalytics;
