import React, { useState } from 'react';
import './Home.css';
import Login from './Login'; // Import the Login component

function Home() {
  const [isOpen, setIsOpen] = useState(false); // Initial state for sidebar
  const [activeTab, setActiveTab] = useState('home'); // State to track active tab
  const [showLogin, setShowLogin] = useState(false); // State for login modal visibility
  const [showSignUp, setShowSignUp] = useState(false); // State for sign-up modal visibility

  const toggleSidebar = () => {
    setIsOpen(!isOpen);
  };

  const handleTabChange = (tab) => {
    setActiveTab(tab);
    if (tab === 'login') {
      setShowLogin(true); // Show the login modal when login tab is clicked
    }
  };

  const handleCloseLogin = () => {
    setShowLogin(false); // Hide the login modal
  };

  const handleShowSignUp = () => {
    setShowLogin(false); // Close the login modal
    setShowSignUp(true); // Show the sign-up modal
  };

  const handleCloseSignUp = () => {
    setShowSignUp(false); // Hide the sign-up modal
  };

  return (
    <div className="container">
      <aside className={`sidebar ${isOpen ? 'open' : 'closed'}`}>
        <div className="sidebar-header">
          <button className="toggle-btn" onClick={toggleSidebar}>
            <div className="line"></div>
            <div className="line"></div>
            <div className="line"></div>
          </button>
        </div>
        <nav className="nav">
          <ul>
            <li>
              <button
                className={`nav-item ${activeTab === 'home' ? 'active' : ''}`}
                onClick={() => handleTabChange('home')}
              >
                <i className={`bi bi-house icon ${activeTab === 'home' ? 'active' : ''} ${isOpen ? '' : 'no-glow'}`}></i>
                <span className={`text ${!isOpen ? 'hidden' : ''}`}>Home</span>
              </button>
            </li>
            <li>
              <button
                className={`nav-item ${activeTab === 'login' ? 'active' : ''}`}
                onClick={() => handleTabChange('login')}
              >
                <i className={`bi bi-lock icon ${activeTab === 'login' ? 'active' : ''} ${isOpen ? '' : 'no-glow'}`}></i>
                <span className={`text ${!isOpen ? 'hidden' : ''}`}>Login</span>
              </button>
            </li>
            <li>
              <button
                className={`nav-item ${activeTab === 'dashboard' ? 'active' : ''}`}
                onClick={() => handleTabChange('dashboard')}
              >
                <i className={`bi bi-bar-chart icon ${activeTab === 'dashboard' ? 'active' : ''} ${isOpen ? '' : 'no-glow'}`}></i>
                <span className={`text ${!isOpen ? 'hidden' : ''}`}>Dashboard</span>
              </button>
            </li>
            <li>
              <button
                className={`nav-item ${activeTab === 'event-planning' ? 'active' : ''}`}
                onClick={() => handleTabChange('event-planning')}
              >
                <i className={`bi bi-calendar icon ${activeTab === 'event-planning' ? 'active' : ''} ${isOpen ? '' : 'no-glow'}`}></i>
                <span className={`text ${!isOpen ? 'hidden' : ''}`}>Event Planning</span>
              </button>
            </li>
            <li>
              <button
                className={`nav-item ${activeTab === 'event-promotion' ? 'active' : ''}`}
                onClick={() => handleTabChange('event-promotion')}
              >
                <i className={`bi bi-megaphone icon ${activeTab === 'event-promotion' ? 'active' : ''} ${isOpen ? '' : 'no-glow'}`}></i>
                <span className={`text ${!isOpen ? 'hidden' : ''}`}>Event Promotion</span>
              </button>
            </li>
            <li>
              <button
                className={`nav-item ${activeTab === 'volunteer-management' ? 'active' : ''}`}
                onClick={() => handleTabChange('volunteer-management')}
              >
                <i className={`bi bi-person-badge icon ${activeTab === 'volunteer-management' ? 'active' : ''} ${isOpen ? '' : 'no-glow'}`}></i>
                <span className={`text ${!isOpen ? 'hidden' : ''}`}>Volunteer Management</span>
              </button>
            </li>
            <li>
              <button
                className={`nav-item ${activeTab === 'resource-management' ? 'active' : ''}`}
                onClick={() => handleTabChange('resource-management')}
              >
                <i className={`bi bi-tools icon ${activeTab === 'resource-management' ? 'active' : ''} ${isOpen ? '' : 'no-glow'}`}></i>
                <span className={`text ${!isOpen ? 'hidden' : ''}`}>Resource Management</span>
              </button>
            </li>
            <li>
              <button
                className={`nav-item ${activeTab === 'feedback-analytics' ? 'active' : ''}`}
                onClick={() => handleTabChange('feedback-analytics')}
              >
                <i className={`bi bi-graph-up icon ${activeTab === 'feedback-analytics' ? 'active' : ''} ${isOpen ? '' : 'no-glow'}`}></i>
                <span className={`text ${!isOpen ? 'hidden' : ''}`}>Feedback & Analytics</span>
              </button>
            </li>
          </ul>
        </nav>
      </aside>
      <main className="main-content">
        <header className="header">
          <h1>Welcome to RGUKT Event Management</h1>
        </header>
        <section className="content">
          {activeTab === 'home' && <p>Manage and participate in events seamlessly.</p>}
          {activeTab === 'login' && <p>Please login to access more features.</p>}
          {activeTab === 'dashboard' && <p>View your dashboard and stats here.</p>}
          {activeTab === 'event-planning' && <p>Plan your events efficiently.</p>}
          {activeTab === 'event-promotion' && <p>Promote your events to a wider audience.</p>}
          {activeTab === 'volunteer-management' && <p>Manage your volunteers effectively.</p>}
          {activeTab === 'resource-management' && <p>Manage resources for your events.</p>}
          {activeTab === 'feedback-analytics' && <p>Analyze feedback and improve your events.</p>}
        </section>
      </main>
      {/* Render the Login modal */}
      <Login show={showLogin} handleClose={handleCloseLogin} handleShowSignUp={handleShowSignUp} />
      {/* Render the SignUp modal */}
      <SignUp show={showSignUp} handleClose={handleCloseSignUp} />
    </div>
  );
}

export default Home;
