import React, { useState } from 'react';
import { Button, Card, Modal, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';

const EventPlanning = () => {
  const [events, setEvents] = useState([]);
  const [showEventForm, setShowEventForm] = useState(false);
  const [eventName, setEventName] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [venue, setVenue] = useState('');
  const [imageUrl, setImageUrl] = useState('');

  const [showProgramForm, setShowProgramForm] = useState(false);
  const [programName, setProgramName] = useState('');
  const [programStartDate, setProgramStartDate] = useState('');
  const [programStartTime, setProgramStartTime] = useState('');
  const [programEndTime, setProgramEndTime] = useState('');
  const [programVenue, setProgramVenue] = useState('');

  // Function to add a new event
  const handleAddEvent = () => {
    setShowEventForm(true);
  };

  // Function to handle submission of the event form
  const handleEventFormSubmit = (e) => {
    e.preventDefault();
    if (endDate < startDate) {
      alert('End date should be on or after start date.');
      return;
    }
    const newEvent = {
      eventName,
      startDate,
      endDate,
      venue,
      imageUrl, // Include imageUrl in the new event object
      eventPrograms: []
    };
    setEvents([...events, newEvent]);
    setEventName('');
    setStartDate('');
    setEndDate('');
    setVenue('');
    setImageUrl('');
    setShowEventForm(false);
  };

  // Function to delete an event
  const handleDeleteEvent = (index) => {
    const updatedEvents = [...events];
    updatedEvents.splice(index, 1);
    setEvents(updatedEvents);
  };

  // Function to add a program to an event
  const handleAddProgram = (index) => {
    setShowProgramForm(true);
    // Set the index of the event for which we are adding a program
    setEventIndex(index);
  };

  // Function to handle submission of the program form
  const handleProgramFormSubmit = (e) => {
    e.preventDefault();
    if (programEndTime <= programStartTime) {
      alert('End time should be greater than start time.');
      return;
    }

    // Ensure updatedEvents[index] exists and initialize if not
    const updatedEvents = [...events];
    updatedEvents[eventIndex].eventPrograms.push({
      programName,
      programStartDate,
      programStartTime,
      programEndTime,
      programVenue
    });
    setEvents(updatedEvents);
    setProgramName('');
    setProgramStartDate('');
    setProgramStartTime('');
    setProgramEndTime('');
    setProgramVenue('');
    setShowProgramForm(false);
  };

  // State to keep track of the index of the event being edited
  const [eventIndex, setEventIndex] = useState(null);

  // Function to toggle the display of programs
  const togglePrograms = (index) => {
    const updatedEvents = [...events];
    updatedEvents[index].showPrograms = !updatedEvents[index].showPrograms;
    setEvents(updatedEvents);
  };

  return (
    <div className="container mt-4">
      <Button variant="primary" onClick={handleAddEvent} className="mb-3">
        + Add Event
      </Button>

      <Modal show={showEventForm} onHide={() => setShowEventForm(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add Event</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleEventFormSubmit}>
            <Form.Group controlId="eventName">
              <Form.Label>Event Name</Form.Label>
              <Form.Control
                type="text"
                value={eventName}
                onChange={(e) => setEventName(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="startDate">
              <Form.Label>Start Date</Form.Label>
              <Form.Control
                type="date"
                value={startDate}
                onChange={(e) => setStartDate(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="endDate">
              <Form.Label>End Date</Form.Label>
              <Form.Control
                type="date"
                value={endDate}
                onChange={(e) => setEndDate(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="venue">
              <Form.Label>Venue</Form.Label>
              <Form.Control
                type="text"
                value={venue}
                onChange={(e) => setVenue(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="imageUrl">
              <Form.Label>Image URL</Form.Label>
              <Form.Control
                type="text"
                value={imageUrl}
                onChange={(e) => setImageUrl(e.target.value)}
                required
              />
            </Form.Group>

            <Button variant="primary" type="submit">
              Create Event
            </Button>
          </Form>
        </Modal.Body>
      </Modal>

      <div className="row">
        {events.map((event, index) => (
          <div key={index} className="col-lg-4 col-md-6 col-sm-12 mb-4">
            <Card style={{ height: '100%' }}>
              {event.imageUrl && (
                <Card.Img variant="top" src={event.imageUrl} alt={event.eventName} />
              )}
              <Card.Body>
                <Card.Title>{event.eventName}</Card.Title>
                <Card.Text>
                  <strong>Date:</strong> {event.startDate} to {event.endDate}
                </Card.Text>
                <Card.Text>
                  <strong>Venue:</strong> {event.venue}
                </Card.Text>
                <div className='clik' style={{display:flex}}>
                <Button variant="danger" onClick={() => handleDeleteEvent(index)}>
                  <i className="bi bi-trash"></i>
                </Button>
                <Button
                  variant="info"
                  className="ml-2"
                  onClick={() => handleAddProgram(index)}
                >
                  + Add Event Program
                </Button>
                <Button
                  variant="secondary"
                  className="ml-2"
                  onClick={() => togglePrograms(index)}
                >
                  {event.showPrograms ? 'Hide Programs' : 'Show Programs'}
                </Button>
                </div>
                {event.showPrograms && event.eventPrograms.length > 0 && (
                  <div className="mt-3">
                    <h5>Programs:</h5>
                    {event.eventPrograms.map((program, progIndex) => (
                      <div key={progIndex}>
                        <p><strong>Program Name:</strong> {program.programName}</p>
                        <p><strong>Date:</strong> {program.programStartDate}</p>
                        <p><strong>Time:</strong> {program.programStartTime} - {program.programEndTime}</p>
                        <p><strong>Venue:</strong> {program.programVenue}</p>
                      </div>
                    ))}
                  </div>
                )}
              </Card.Body>
            </Card>
          </div>
        ))}
      </div>

      <Modal show={showProgramForm} onHide={() => setShowProgramForm(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add Event Program</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleProgramFormSubmit}>
            <Form.Group controlId="programName">
              <Form.Label>Program Name</Form.Label>
              <Form.Control
                type="text"
                value={programName}
                onChange={(e) => setProgramName(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="programStartDate">
              <Form.Label>Date</Form.Label>
              <Form.Control
                type="date"
                value={programStartDate}
                onChange={(e) => setProgramStartDate(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="programStartTime">
              <Form.Label>Start Time</Form.Label>
              <Form.Control
                type="time"
                value={programStartTime}
                onChange={(e) => setProgramStartTime(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="programEndTime">
              <Form.Label>End Time</Form.Label>
              <Form.Control
                type="time"
                value={programEndTime}
                onChange={(e) => setProgramEndTime(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="programVenue">
              <Form.Label>Venue</Form.Label>
              <Form.Control
                type="text"
                value={programVenue}
                onChange={(e) => setProgramVenue(e.target.value)}
                required
              />
            </Form.Group>

            <Button variant="primary" type="submit">
              Save Program
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
};

export default EventPlanning;
