import React, { useEffect, useState } from 'react';
import './AdminVolunteer.css';
import 'bootstrap/dist/css/bootstrap.min.css';

function EventPromotion() {
  const [eventVolunteerData, setEventVolunteerData] = useState([]);
  const [selectedFiles, setSelectedFiles] = useState({});
  const [previewImages, setPreviewImages] = useState({});
  const [uploadedImages, setUploadedImages] = useState({});  // New state for fetched images

  // Fetch event and volunteer data
  useEffect(() => {
    // Fetch event and volunteer data
    fetch('http://localhost:5000/api/volunteers/events')
      .then((response) => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then((data) => {
        console.log('Fetched event and volunteer data:', data);
        setEventVolunteerData(data);
      })
      .catch((error) => console.error('Error fetching event and volunteer data:', error));

    // Fetch uploaded images for each event
    fetchUploadedImages();
  }, []);

  // Fetch uploaded images for all events
  const fetchUploadedImages = () => {
    eventVolunteerData.forEach((eventData) => {
      fetch(`http://localhost:5000/api/images/${eventData._id}/uploaded-images`)
        .then((response) => {
          if (!response.ok) {
            throw new Error('Failed to fetch uploaded images');
          }
          return response.json();
        })
        .then((data) => {
          console.log('Fetched uploaded images:', data);
          setUploadedImages((prevImages) => ({
            ...prevImages,
            [eventData._id]: data.images, // Store images by eventId
          }));
        })
        .catch((error) => console.error('Error fetching uploaded images:', error));
    });
  };

  // Handle file selection and convert to Base64
  const handleFileChange = (event, eventId) => {
    const files = event.target.files;
    const filesArray = Array.from(files);

    const filePreviews = [];
    const fileBase64Array = [];

    filesArray.forEach((file) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => {
        const base64Image = {
          fileName: file.name,
          base64: reader.result,
        };
        const previewImage = {
          fileName: file.name,
          url: reader.result,
        };

        // Update the selected files state for this event ID
        setSelectedFiles((prevFiles) => {
          const updatedFiles = { ...prevFiles };
          if (!updatedFiles[eventId]) updatedFiles[eventId] = [];
          updatedFiles[eventId] = [...updatedFiles[eventId], base64Image];
          return updatedFiles;
        });

        // Update the preview images state for this event ID
        setPreviewImages((prevPreviews) => {
          const updatedPreviews = { ...prevPreviews };
          if (!updatedPreviews[eventId]) updatedPreviews[eventId] = [];
          updatedPreviews[eventId] = [...updatedPreviews[eventId], previewImage];
          return updatedPreviews;
        });
      };
    });
  };

  // Handle image upload to the server
  const handleUpload = (eventId) => {
    const files = selectedFiles[eventId];
    if (!files || files.length === 0) return;

    // Prepare the payload with Base64 images and eventId
    const formData = {
      images: files.map((file) => ({ base64: file.base64 })),
    };

    fetch(`http://localhost:5000/api/images/${eventId}/upload-images`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then((response) => {
        if (!response.ok) {
          throw new Error('Failed to upload images');
        }
        return response.json();
      })
      .then((data) => {
        console.log('Images uploaded successfully:', data);
        alert('Images uploaded successfully!');
        // Clear the preview and selected files for this specific event
        setSelectedFiles((prevFiles) => ({
          ...prevFiles,
          [eventId]: [],
        }));
        setPreviewImages((prevPreviews) => ({
          ...prevPreviews,
          [eventId]: [],
        }));
        // After uploading, fetch the updated images
        fetchUploadedImages();
      })
      .catch((error) => console.error('Error uploading images:', error));
  };

  return (
    <div className="container mt-4">
      {eventVolunteerData.map((eventData) => (
        <div key={eventData._id} className="row mb-4">
          <div className="col-12">
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">{eventData.eventName}</h5>
                <h6 className="card-subtitle mb-2 text-muted">{eventData._id}</h6>
                <div className="mb-3">
                  <label htmlFor={`fileInput-${eventData._id}`} className="form-label">
                    Upload Images
                  </label>
                  <input
                    type="file"
                    id={`fileInput-${eventData._id}`}
                    className="form-control"
                    multiple
                    onChange={(event) => handleFileChange(event, eventData._id)}
                  />
                </div>

                {/* Display image previews for selected files */}
                <div className="row mt-3">
                  {previewImages[eventData._id] && previewImages[eventData._id].length > 0 ? (
                    previewImages[eventData._id].map((image, imgIndex) => (
                      <div key={imgIndex} className="col-12 col-sm-6 col-md-4 mb-3">
                        <div className="card">
                          <img
                            src={image.url}
                            className="card-img-top img-thumbnail"
                            alt={image.fileName}
                          />
                          <div className="card-body">
                            <p className="card-text">{image.fileName}</p>
                          </div>
                        </div>
                      </div>
                    ))
                  ) : (
                    <p className="text-muted">No images selected for preview.</p>
                  )}
                </div>

                {/* Display uploaded images fetched from the server */}
                <div className="row mt-3">
                  {uploadedImages[eventData._id] && uploadedImages[eventData._id].length > 0 ? (
                    uploadedImages[eventData._id].map((image, imgIndex) => (
                      <div key={imgIndex} className="col-12 col-sm-6 col-md-4 mb-3">
                        <div className="card">
                          <img
                            src={image.base64}
                            className="card-img-top img-thumbnail"
                            alt={`Uploaded image ${imgIndex}`}
                          />
                          <div className="card-body">
                            <p className="card-text">Uploaded Image</p>
                          </div>
                        </div>
                      </div>
                    ))
                  ) : (
                    <p className="text-muted">No uploaded images available.</p>
                  )}
                </div>

                <button
                  className="btn btn-primary mt-2"
                  onClick={() => handleUpload(eventData._id)}
                >
                  Upload
                </button>
              </div>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

export default EventPromotion;
