// App.js
import React, { useState } from 'react';
import { Container, Button, Card, Modal, Form } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css'; // Import the custom CSS file

const VolunteerManagement = () => {
  const [showAddVolunteers, setShowAddVolunteers] = useState(false);
  const [showRegistration, setShowRegistration] = useState(false);
  const [showScheduling, setShowScheduling] = useState(false);
  const [showChat, setShowChat] = useState(false);
  const [volunteersEmails, setVolunteersEmails] = useState('');
  const [eventName, setEventName] = useState('');
  const [scheduleFields, setScheduleFields] = useState([{ workDetail: '', workEmail: '' }]);
  const [chatEmail, setChatEmail] = useState('');
  const [chatMessage, setChatMessage] = useState('');

  const handleAddVolunteersSubmit = (e) => {
    e.preventDefault();
    const emails = volunteersEmails.split(',').map(email => email.trim());
    console.log('Volunteers added:', emails);
    setShowAddVolunteers(false);
    setVolunteersEmails('');
  };

  const handleRegistrationSubmit = (e) => {
    e.preventDefault();
    console.log('Registered for event:', eventName);
    setShowRegistration(false);
    setEventName('');
  };

  const handleSchedulingSubmit = (e) => {
    e.preventDefault();
    console.log('Schedule saved:', scheduleFields);
    setShowScheduling(false);
    setScheduleFields([{ workDetail: '', workEmail: '' }]);
  };

  const handleChatSubmit = (e) => {
    e.preventDefault();
    console.log('Chat message sent to', chatEmail, ':', chatMessage);
    setShowChat(false);
    setChatEmail('');
    setChatMessage('');
  };

  const handleAddScheduleField = () => {
    setScheduleFields([...scheduleFields, { workDetail: '', workEmail: '' }]);
  };

  const handleScheduleFieldChange = (index, field, value) => {
    const updatedFields = [...scheduleFields];
    updatedFields[index] = { ...updatedFields[index], [field]: value };
    setScheduleFields(updatedFields);
  };

  return (
    <Container className="mt-5">
      <h1 className="text-center mb-4">Volunteers Management</h1>
      <div className="d-flex justify-content-center mb-4">
        <Button variant="success" onClick={() => setShowAddVolunteers(true)}>
          Add Volunteers
        </Button>
      </div>
      <div className="row">
        <div className="col-md-6 mb-4">
          <Card className="h-100 shadow-sm">
            <Card.Body>
              <Card.Title>Registration</Card.Title>
              <Card.Text>Register for events.</Card.Text>
              <Button variant="primary" onClick={() => setShowRegistration(true)}>
                Open Registration
              </Button>
            </Card.Body>
          </Card>
        </div>
        <div className="col-md-6 mb-4">
          <Card className="h-100 shadow-sm">
            <Card.Body>
              <Card.Title>Scheduling</Card.Title>
              <Card.Text>Manage event schedules.</Card.Text>
              <Button variant="primary" onClick={() => setShowScheduling(true)}>
                Open Scheduling
              </Button>
            </Card.Body>
          </Card>
        </div>
      </div>

      {/* Add Volunteers Modal */}
      <Modal show={showAddVolunteers} onHide={() => setShowAddVolunteers(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Add Volunteers</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleAddVolunteersSubmit}>
            <Form.Group controlId="volunteersEmails">
              <Form.Label>Email IDs (comma separated)</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={volunteersEmails}
                onChange={(e) => setVolunteersEmails(e.target.value)}
                required
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Add Volunteers
            </Button>
          </Form>
        </Modal.Body>
      </Modal>

      {/* Registration Modal */}
      <Modal show={showRegistration} onHide={() => setShowRegistration(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Event Registration</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleRegistrationSubmit}>
            <Form.Group controlId="eventName">
              <Form.Label>Event Name</Form.Label>
              <Form.Control
                as="select"
                value={eventName}
                onChange={(e) => setEventName(e.target.value)}
                required
              >
                <option>Chess</option>
                <option>Rangoli</option>
                <option>Carrom</option>
                <option>Hackathon</option>
                <option>Workshop</option>
                <option>Cricket</option>
              </Form.Control>
            </Form.Group>
            <Button variant="primary" type="submit">
              Register
            </Button>
          </Form>
        </Modal.Body>
      </Modal>

      {/* Scheduling Modal */}
      <Modal show={showScheduling} onHide={() => setShowScheduling(false)} size="lg">
        <Modal.Header closeButton>
          <Modal.Title>Event Scheduling</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSchedulingSubmit}>
            {scheduleFields.map((field, index) => (
              <div key={index} className="mb-3">
                <Form.Group controlId={`workDetails-${index}`}>
                  <Form.Label>Work Details</Form.Label>
                  <Form.Control
                    type="text"
                    value={field.workDetail}
                    onChange={(e) => handleScheduleFieldChange(index, 'workDetail', e.target.value)}
                    required
                  />
                </Form.Group>
                <Form.Group controlId={`workEmail-${index}`}>
                  <Form.Label>Email ID</Form.Label>
                  <Form.Control
                    type="email"
                    value={field.workEmail}
                    onChange={(e) => handleScheduleFieldChange(index, 'workEmail', e.target.value)}
                    required
                  />
                </Form.Group>
              </div>
            ))}
            <Button variant="secondary" onClick={handleAddScheduleField}>
              Add Work Field
            </Button>
            <Button variant="primary" type="submit">
              Save Schedule
            </Button>
          </Form>
        </Modal.Body>
      </Modal>

      {/* Chat Icon */}
      <div className="chat-icon" onClick={() => setShowChat(true)}>
        <span>&#x2709;</span>
      </div>

      {/* Chat Modal */}
      <Modal show={showChat} onHide={() => setShowChat(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Chat</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleChatSubmit}>
            <Form.Group controlId="chatEmail">
              <Form.Label>Email ID</Form.Label>
              <Form.Control
                type="email"
                value={chatEmail}
                onChange={(e) => setChatEmail(e.target.value)}
                required
              />
            </Form.Group>
            <Form.Group controlId="chatMessage">
              <Form.Label>Message</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={chatMessage}
                onChange={(e) => setChatMessage(e.target.value)}
                required
              />
            </Form.Group>
            <Button variant="primary" type="submit">
              Send Message
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </Container>
  );
};

export default VolunteerManagement;
