import React, { useState } from 'react';
import './EventTimeline.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';
import { Modal, Button, Form, Alert } from 'react-bootstrap';

function EventTimeline() {
  const [eventCount, setEventCount] = useState('');
  const [events, setEvents] = useState([]);
  const [showEventModal, setShowEventModal] = useState(false);
  const [showAssignModal, setShowAssignModal] = useState(false);
  const [currentEvent, setCurrentEvent] = useState(null);
  const [overview, setOverview] = useState(null);

  const handleCreateTimeline = () => {
    const count = parseInt(eventCount);
    if (isNaN(count) || count <= 0) return;

    const newEvents = [];
    for (let i = 0; i < count; i++) {
      newEvents.push({ name: `Event ${i + 1}`, time: '', description: '', deadline: '', venue: '' });
    }
    setEvents(newEvents);
    setEventCount('');
  };

  const handleEditEvent = (index) => {
    setCurrentEvent({ ...events[index], index });
    setShowEventModal(true);
  };

  const handleAssignWork = (index) => {
    setCurrentEvent({ ...events[index], index });
    setShowAssignModal(true);
  };

  const handleSaveEvent = (e) => {
    e.preventDefault();
    const updatedEvents = [...events];
    updatedEvents[currentEvent.index] = currentEvent;
    setEvents(updatedEvents);
    setShowEventModal(false);
  };

  const handleSaveAssignment = (e) => {
    e.preventDefault();
    alert('Work assigned successfully!');
    setShowAssignModal(false);
  };

  const handleAddEvent = (index, position) => {
    const newEvents = [...events];
    const newEvent = { name: `Event ${newEvents.length + 1}`, time: '', description: '', deadline: '', venue: '' };
    if (position === 'above') {
      newEvents.splice(index, 0, newEvent);
    } else {
      newEvents.splice(index + 1, 0, newEvent);
    }
    setEvents(newEvents);
  };

  const handleRemoveEvent = (index) => {
    const newEvents = events.filter((_, i) => i !== index);
    setEvents(newEvents);
  };

  const handleOverviewClick = (event) => {
    setOverview(event);
  };

  return (
    <div className="container">
      <h2 className="text-center mt-5">Event Timeline</h2>
      <div className="form-group" id="eventFormContainer">
        <label htmlFor="eventCount">Number of Events:</label>
        <input
          type="number"
          className="form-control"
          id="eventCount"
          min="1"
          placeholder="Enter number of events"
          value={eventCount}
          onChange={(e) => setEventCount(e.target.value)}
        />
        <Button className="btn btn-primary mt-3" onClick={handleCreateTimeline}>
          <i className="fas fa-check"></i>
        </Button>
      </div>
      <div className="row">
        <div className="col-md-8">
          <ul className="timeline" id="timeline">
            {events.map((event, index) => (
              <li key={index} onClick={() => handleOverviewClick(event)}>
                <div className="timeline-panel">
                  <h5 className="timeline-title">{event.name}</h5>
                  <p>
                    <small className="text-muted timeline-time">{event.time}</small>
                  </p>
                  <p className="timeline-description">{event.description}</p>
                  <div className="button-group">
                    <Button variant="secondary" size="sm" onClick={() => handleAddEvent(index, 'above')}>
                      <i className="fas fa-arrow-up"></i>
                    </Button>
                    <Button variant="secondary" size="sm" onClick={() => handleAddEvent(index, 'below')}>
                      <i className="fas fa-arrow-down"></i>
                    </Button>
                    <Button variant="warning" size="sm" onClick={() => handleEditEvent(index)}>
                      <i className="fas fa-edit"></i>
                    </Button>
                    <Button variant="danger" size="sm" onClick={() => handleRemoveEvent(index)}>
                      <i className="fas fa-trash-alt"></i>
                    </Button>
                    <Button variant="info" size="sm" onClick={() => handleAssignWork(index)}>
                      <i className="fas fa-tasks"></i>
                    </Button>
                  </div>
                </div>
              </li>
            ))}
          </ul>
          {events.length === 0 && (
            <Alert id="successMessage" className="mt-5">
              All events are completed successfully!
            </Alert>
          )}
        </div>
        <div className="col-md-4">
          {overview && (
            <div className="overview" id="eventOverview">
              <div className="overview-heading">Event Overview</div>
              <div className="overview-body">
                <div className="overview-title">{overview.name}</div>
                <div className="overview-time">Time: {overview.time}</div>
                <div className="overview-description">Description: {overview.description}</div>
                <div className="overview-coordinator">Coordinator: {overview.coordinator}</div>
                <div className="overview-venue">Venue: {overview.venue}</div>
                <div className="overview-resources">No resources available</div>
                <div className="overview-photos">
                  <img
                    src="https://via.placeholder.com/150"
                    className="overview-photo"
                    alt="Event Photo"
                  />
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      {/* Modal for Editing Event */}
      <Modal show={showEventModal} onHide={() => setShowEventModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Event</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form id="editEventForm" onSubmit={handleSaveEvent}>
            <Form.Group>
              <Form.Label>Event Name:</Form.Label>
              <Form.Control
                type="text"
                required
                value={currentEvent?.name || ''}
                onChange={(e) => setCurrentEvent({ ...currentEvent, name: e.target.value })}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Event Time:</Form.Label>
              <Form.Control
                type="datetime-local"
                required
                value={currentEvent?.time || ''}
                onChange={(e) => setCurrentEvent({ ...currentEvent, time: e.target.value })}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Event Description:</Form.Label>
              <Form.Control
                as="textarea"
                rows={3}
                value={currentEvent?.description || ''}
                onChange={(e) => setCurrentEvent({ ...currentEvent, description: e.target.value })}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Set Deadline:</Form.Label>
              <Form.Control
                type="datetime-local"
                value={currentEvent?.deadline || ''}
                onChange={(e) => setCurrentEvent({ ...currentEvent, deadline: e.target.value })}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Book Venue:</Form.Label>
              <Form.Control
                type="text"
                value={currentEvent?.venue || ''}
                onChange={(e) => setCurrentEvent({ ...currentEvent, venue: e.target.value })}
              />
            </Form.Group>
            <Button type="submit" className="btn btn-primary">
              <i className="fas fa-save"></i>
            </Button>
          </Form>
        </Modal.Body>
      </Modal>

      {/* Modal for Assigning Work */}
      <Modal show={showAssignModal} onHide={() => setShowAssignModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Assign Work</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form id="assignWorkForm" onSubmit={handleSaveAssignment}>
            <Form.Group>
              <Form.Label>Event Name:</Form.Label>
              <Form.Control type="text" readOnly value={currentEvent?.name || ''} />
            </Form.Group>
            <Form.Group>
              <Form.Label>Coordinator Name:</Form.Label>
              <Form.Control
                type="text"
                required
                value={currentEvent?.coordinator || ''}
                onChange={(e) => setCurrentEvent({ ...currentEvent, coordinator: e.target.value })}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Mail ID:</Form.Label>
              <Form.Control
                type="email"
                required
                value={currentEvent?.mailId || ''}
                onChange={(e) => setCurrentEvent({ ...currentEvent, mailId: e.target.value })}
              />
            </Form.Group>
            <Form.Group>
              <Form.Label>Event Description:</Form.Label>
              <Form.Control as="textarea" rows={3} readOnly value={currentEvent?.description || ''} />
            </Form.Group>
            <Button type="submit" className="btn btn-primary">
              <i className="fas fa-tasks"></i> Assign Work
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default EventTimeline;
