import React, { useState } from 'react';
import axios from 'axios';

const FeedbackAnalytics = ({ eventId }) => {
  const [rating, setRating] = useState(1); // Default to 1 star
  const [text, setText] = useState('');

  const handleRatingChange = (e) => {
    setRating(parseInt(e.target.value));
  };

  const handleTextChange = (e) => {
    setText(e.target.value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('/api/feedback/submitFeedback', {
        eventId,
        userId: 'user_id_here', // Replace with actual user ID or fetch from authentication
        rating,
        text,
      });

      console.log('Feedback submitted:', response.data);
      // Optionally, you can update state or show a success message
    } catch (error) {
      console.error('Error submitting feedback:', error);
      // Handle error, show error message, etc.
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <label>
        Rating:
        <select value={rating} onChange={handleRatingChange}>
          {[1, 2, 3, 4, 5].map((num) => (
            <option key={num} value={num}>
              {num}
            </option>
          ))}
        </select>
      </label>
      <br />
      <label>
        Feedback (max 30 words):
        <textarea value={text} onChange={handleTextChange} maxLength={30} />
      </label>
      <br />
      <button type="submit">Submit Feedback</button>
    </form>
  );
};

export default FeedbackAnalytics;
