import React, { useEffect, useState } from 'react';
import './AdminVolunteer.css';

function EventPromotion() {
  const [eventVolunteerData, setEventVolunteerData] = useState([]);
  const [eventName, setEventName] = useState('');
  const [images, setImages] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5000/api/events')
      .then((response) => response.json())
      .then((data) => setEventVolunteerData(data))
      .catch((error) => console.error('Error fetching event data:', error));
  }, []);

  const handleImageUpload = (e) => {
    setImages(e.target.files);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('eventName', eventName);
    Array.from(images).forEach((image) => {
      formData.append('images', image);
    });

    try {
      const response = await fetch('http://localhost:5000/api/events', {
        method: 'POST',
        body: formData,
      });
      const newEvent = await response.json();
      setEventVolunteerData([...eventVolunteerData, newEvent]);
      setEventName('');
      setImages([]);
    } catch (error) {
      console.error('Error uploading images:', error);
    }
  };

  return (
    <div>
      <h1>Event Promotion</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Event Name"
          value={eventName}
          onChange={(e) => setEventName(e.target.value)}
          required
        />
        <input
          type="file"
          multiple
          accept="image/*"
          onChange={handleImageUpload}
          required
        />
        <button type="submit">Upload Event</button>
      </form>

      {eventVolunteerData.map((eventData, index) => (
        <div key={index} className="event-container">
          <h2>{eventData.eventName}</h2>
          {eventData.images.map((image, imgIndex) => (
            <img key={imgIndex} src={`http://localhost:5000/${image}`} alt={`Event ${eventData.eventName}`} style={{ width: '100px', height: '100px', margin: '5px' }} />
          ))}
        </div>
      ))}
    </div>
  );
}

export default EventPromotion;
