import React, { useState } from 'react';
import { Modal, Button, Form } from 'react-bootstrap';
import axios from 'axios';

function SignUp({ show, handleClose }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('');
  const [isVolunteer, setIsVolunteer] = useState(false);

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post('/api/signup', { email, password, role, isVolunteer });
      console.log('SignUp Success:', response);
      // Handle successful sign-up
    } catch (error) {
      console.log('SignUp Failed:', error);
      // Handle sign-up failure
    }
  };

  return (
    <Modal show={show} onHide={handleClose} centered>
      <Modal.Header closeButton>
        <Modal.Title>Sign Up</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="mb-3" controlId="signupEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control 
              type="email" 
              placeholder="Enter your email" 
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="signupPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control 
              type="password" 
              placeholder="Enter your password" 
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </Form.Group>

          <Form.Group className="mb-3" controlId="roleSelect">
            <Form.Label>Select your role</Form.Label>
            <Form.Control 
              as="select" 
              value={role}
              onChange={(e) => setRole(e.target.value)}
              required
            >
              <option value="">Select a role</option>
              <option value="faculty">Faculty</option>
              <option value="event-manager">Event Manager</option>
              <option value="student">Student</option>
            </Form.Control>
          </Form.Group>

          {role === 'student' && (
            <Form.Group className="mb-3" controlId="volunteerCheck">
              <Form.Check 
                type="checkbox" 
                label="Are you a volunteer?" 
                checked={isVolunteer}
                onChange={(e) => setIsVolunteer(e.target.checked)}
              />
            </Form.Group>
          )}

          <Button variant="primary" type="submit">
            Sign Up
          </Button>
        </Form>
      </Modal.Body>
    </Modal>
  );
}

export default SignUp;
