import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import '@fortawesome/fontawesome-free/css/all.min.css';
import './Home.css';
import Login from './Login';

function Home() {
  const [activeTab, setActiveTab] = useState('home');
  const [showLogin, setShowLogin] = useState(false);

  const handleLoginClick = () => {
    setShowLogin(true);
  };

  const handleCloseLogin = () => {
    setShowLogin(false);
  };

  return (
    <div className="container">
      <aside className="sidebar closed">
        <div className="sidebar-header">
          <h2>RGUKT Event Management</h2>
        </div>
        <nav className="nav">
          <ul>
            <li>
              <button onClick={() => setActiveTab('home')}>
                <i className="fas fa-home"></i>
                <span className="nav-text">Home</span>
              </button>
            </li>
            <li>
              <button onClick={handleLoginClick}>
                <i className="fas fa-sign-in-alt"></i>
                <span className="nav-text">Login</span>
              </button>
            </li>
            {/* Other nav items */}
          </ul>
        </nav>
      </aside>
      <main className="main-content">
        <header className="header">
          <h1>Welcome to RGUKT Event Management</h1>
        </header>
        <section className="content">
          {activeTab === 'home' && <p>Manage and participate in events seamlessly.</p>}
          {/* Other content based on active tab */}
          {showLogin && <Login show={showLogin} handleClose={handleCloseLogin} />}
        </section>
      </main>
    </div>
  );
}

export default Home;
