import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EventPromotion = () => {
  const [eventName, setEventName] = useState('');
  const [images, setImages] = useState([]);
  const [selectedFiles, setSelectedFiles] = useState([]);
  const user = JSON.parse(localStorage.getItem('userData'));
  const userType = user ? user[2] : '';

  useEffect(() => {
    if (userType === 'event-manager') {
      fetchImages();
    }
  }, [userType]);

  const fetchImages = async () => {
    try {
      const response = await axios.get(`/api/promotions/images/${eventName}`);
      setImages(response.data);
    } catch (error) {
      console.error('Error fetching images:', error);
    }
  };

  const handleFileChange = (e) => {
    setSelectedFiles([...e.target.files]);
  };

  const handleUpload = async () => {
    const formData = new FormData();
    formData.append('eventName', eventName);
    for (const file of selectedFiles) {
      formData.append('images', file);
    }

    try {
      await axios.post('/api/promotions/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' }
      });
      fetchImages();
    } catch (error) {
      console.error('Error uploading images:', error);
    }
  };

  const handleDelete = async (publicId) => {
    try {
      await axios.delete(`/api/promotions/images/delete/${publicId}`);
      fetchImages();
    } catch (error) {
      console.error('Error deleting image:', error);
    }
  };

  return (
    <div>
      <h1>Promotion Images</h1>
      {userType === 'event-manager' && (
        <div>
          <input
            type="text"
            placeholder="Event Name"
            value={eventName}
            onChange={(e) => setEventName(e.target.value)}
          />
          <input
            type="file"
            multiple
            onChange={handleFileChange}
          />
          <button onClick={handleUpload}>Upload Images</button>
        </div>
      )}
      <div>
        {images.map((img) => (
          <div key={img.publicId}>
            <img src={img.url} alt="" style={{ width: '200px', height: 'auto' }} />
            {userType === 'event-manager' && (
              <button onClick={() => handleDelete(img.publicId)}>Delete</button>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default EventPromotion;
